INSERT INTO `items` (`id`, `title`, `excerpt`, `content`, `image`, `category`, `featured`, `views`, `created_at`, `updated_at`, `author`) VALUES
(1, 'Biến đổi khí hậu và mối liên quan với các siêu bão', 'Sự ấm lên của đại dương do biến đổi khí hậu đang góp phần khiến các cơn bão mạnh lên nhanh hơn, hình thành những siêu bão có sức tàn phá lớn.', '<img src=\"https://cdn-images.vtv.vn/thumb_w/650/66349b6076cb4dee98746cf1/2024/10/28/afp--20240925--36h83e8--v4--highres--tormentatropicalheleneprovocalluviaseneloestede-17877713900337414340595-20790493328534644511526.jpg\">\r\n\r\nTheo các nhà khoa học, Yagi vốn là một cơn bão nhiệt đới với sức gió tối đa 90km/h nhưng đã nhanh chóng mạnh lên với sức gió lên tới gần 240km/h - tương đương với bão cấp 4 ở Đại Tây Dương nhờ điều kiện thuận lợi là nước biển ấm. Phân tích đường đi của bão, các nhà khoa học phát hiện, nhiệt độ nước biển quanh Philippines trung bình trên 31 độ C. Trong khi đó, nhiệt độ nước biển từ 29 độ C trở lên có thể cung cấp đủ năng lượng để cơn bão đạt đến sức mạnh tối đa. Yếu tố khác như độ ẩm dồi dào trong khí quyển cũng quan trọng để cơn bão phát triển.\r\n\r\nBiến đổi khí hậu đang làm các cơn bão mang nhiều hơi nước hơn, nhiều gió hơn và dữ dội hơn. Ngoài ra, cũng có bằng chứng cho thấy biến đổi khí hậu khiến bão di chuyển chậm hơn, theo đó bão có thể trút nhiều nước hơn vào một chỗ. Biến đổi khí hậu cũng có thể làm tăng lượng mưa do cơn bão mang lại.\r\n\r\nThế giới đã ấm lên 1,1 độ C so với mức nhiệt độ trung bình thời kỳ tiền công nghiệp. Các nhà khoa học tại Cơ quan Quản lý Khí quyển và Đại dương Quốc gia Mỹ (NOAA) dự đoán: Khi nhiệt độ tăng thêm 2 độ C, tốc độ gió của bão có thể tăng đến 10%. NOAA cũng dự báo, tỷ lệ bão đạt đến mức dữ dội nhất có thể tăng khoảng 10% trong thế kỷ này.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/afp--20240929--2174622527--v4--highres--hurricanehelenecausesmassivefloodingacrosssw-08605048931849379841470.jpg\">\r\n\r\nTờ Japan Times dẫn một phân tích nhanh của Đại học Hoàng gia London cho thấy, khả năng tàn phá của bão Shanshan đổ bộ vào Nhật Bản hồi cuối tháng 8 vừa qua cao hơn 26% là do biến đổi khí hậu. “Không thể phủ nhận sự nóng lên toàn cầu đã làm tăng nhiệt độ trên bề mặt đại dương và trong biển” - Kosuke Ito, Phó Giáo sư và chuyên gia về thời tiết khắc nghiệt tại Đại học Kyoto và Đại học Quốc gia Yokohama (Nhật Bản) cho biết. “Bão lấy năng lượng từ hơi nước bốc lên từ đại dương ấm. Vì vậy, nhiệt độ bề mặt đại dương càng cao, bão càng mạnh”. Ông cho biết, so với những năm 1980, nhiệt độ bề mặt đại dương đã tăng 1 độ C, lưu ý rằng 1 độ C ấm lên sẽ dẫn đến sự gia tăng 7% về thể tích hơi nước, cũng như làm giảm áp suất trung tâm của cơn bão khoảng 10 hectopascal. Áp suất trung tâm càng thấp, cơn bão càng trở nên dữ dội hơn. Kosuke Ito cho biết, dữ liệu đáng tin cậy trong 40 năm qua không cho thấy bản thân các cơn bão đã trở nên mạnh hơn ở khu vực Tây Thái Bình Dương. Nhưng rõ ràng là sự nóng lên đã khiến các cơn bão giải phóng nhiều mưa hơn - ông nói thêm.\r\n\r\nCòn theo Euronews, với việc một số cơn bão nhiệt đới cực mạnh xuất hiện trong thập kỷ qua, cùng viễn cảnh sẽ còn nhiều siêu bão hơn nữa, các chuyên gia đề xuất một loại bão mới: bão cấp 6, dành cho những cơn bão có sức gió vượt quá 309km/giờ. Thang đo Saffir-Simpson 5 cấp truyền thống, được phát triển cách đây hơn 50 năm, có thể không còn phản ánh hết sức tàn phá thực sự của những cơn bão mạnh nhất.\r\n\r\nBiến đổi khí hậu đang khiến những cơn bão tồi tệ nhất trở nên tồi tệ hơn nữa, Michael Wehner, nhà khoa học khí hậu tại Phòng Thí nghiệm quốc gia Lawrence Berkley (California, Mỹ) cho biết.\r\n\r\nMột điều đáng chú ý là biến đổi khí hậu có thể không tạo thêm nhiều cơn bão nhưng được cho là có mối quan hệ mật thiết với việc các cơn bão ngày càng dữ dội hơn, hình thành nên các siêu bão với những ảnh hưởng vô cùng lớn đối với đời sống, kinh tế, gây thiệt hại nghiêm trọng cả về cơ sở hạ tầng, con người và việc phục hồi có thể mất rất nhiều thời gian, công sức...\r\n\r\nTheo Earth.org, vào tháng 5 vừa qua, Cơ quan Quản lý Khí quyển và Đại dương Quốc gia Mỹ (NOAA) dự đoán, số cơn bão ở Đại Tây Dương năm nay có khả năng sẽ cao hơn bình thường do sự kết hợp giữa mức nhiệt gần kỷ lục ở Đại Tây Dương và sự phát triển của hiện tượng La Niña ở Thái Bình Dương.\r\n\r\nNOAA dự đoán có khoảng 17 đến 25 cơn bão được đặt tên trong năm nay, trong đó có 8 đến 13 cơn bão sẽ trở thành cuồng phong; 4 đến 7 cơn bão sẽ trở thành siêu bão. Nếu đúng như vậy, theo Tổ chức Khí tượng Thế giới (WMO), năm 2024 sẽ là năm thứ chín liên tiếp ghi nhận nhiều bão hơn bình thường.\r\n\r\nTrong số 10 cơn bão nhiệt đới xảy ra ở Đại Tây Dương gây thiệt hại lớn nhất thế giới được xếp hạng, đứng đầu là cơn bão Katrina năm 2005. Cơn bão này đã tấn công thành phố New Orleans của Mỹ và các khu vực xung quanh, khiến 1.392 người thiệt mạng. Thiệt hại ước tính là 186,3 tỷ USD. Tiếp theo là cơn bão Harvey năm 2017, gây thiệt hại kinh tế 157 tỷ USD và cơn bão Maria năm 2017, gây thiệt hại 112 tỷ USD.\r\n\r\nỞ Thái Bình Dương, cơn bão Doksuri năm 2023 gây thiệt hại hơn 28 tỷ USD, chủ yếu ở Trung Quốc. Tiếp theo là cơn bão Hagibis đổ bộ vào Nhật Bản năm 2019, gây thiệt hại 18 tỷ USD.\r\n\r\n(Theo Earth.org)', 'Nơi bão Helene đi qua.png', 1, 0, 589, '2024-10-29 04:28:29', '2024-10-29 04:28:29', 1),
(4, 'Trùng tu di tích: Lạ là bị la', 'Những ngày qua, trùng tu Chùa Cầu - Hội An là 1 trong những chủ đề được bàn tán rôm rả nhất. Khen có nhưng chê cũng nhiều.', '16h30 ngày mai (3/8) - sau 2 năm trùng tu, Chùa Cầu Hội An sẽ chính thức được khánh thành trong chuỗi sự kiện giao lưu văn hóa Hội An - Nhật Bản lần thứ 20 năm 2024. Hành trình đến ngày khánh thành của cây cầu 400 năm tuổi này quả thật rất sóng gió.\r\n\r\nMột cách tích cực thì có yêu chùa Cầu, công chúng mới quan tâm để ý đến vậy, nhận xét khen chê. Đến thời điểm này, có thể nói: bằng sự kịp thời - công khai - minh bạch thông tin, quá trình trùng tu chùa Cầu đã \"thoát pressing\" thành công trước những ý kiến của cộng đồng mạng.\r\n\r\nGiờ có lẽ là lúc những công trình kiến trúc đi cùng năm tháng khác, như Nhà thờ Đức Bà ở TP. Hồ Chí Minh đang trùng tu chẳng hạn, hoặc bất kỳ những khu di tích nào khác đang hoặc sắp trong quá trình trùng tu cần nhìn vào câu chuyện của chùa Cầu - Hội An để sẵn sàng tâm thế. Bởi không sớm thì muộn khi quá trình tu bổ được hoàn thành, rất có thể họ sẽ lại phải đối diện với những lời nhận xét khen chê như thế. Trong quá khứ không thiếu gì những trường hợp tương tự đã từng xảy ra.\r\n\r\nNhiều di tích vướng phải tranh cãi sau khi trùng tu\r\n\r\nNhiều ý kiến trái chiều trên nhiều diễn đàn vào năm 2019 khi Cổng Ngọ Môn (Đại Nội Huế) được các chuyên gia sử dụng máy phun rửa áp lực cao nhằm loại bỏ các hiện tượng ô nhiễm sinh học bám lâu năm trên các bức tường. Việc thực hiện làm sạch, trùng tu Ngọ Môn mang lại diện mạo khá lạ lẫm cho công trình dù đây mới là màu sắc gần như nguyên bản của di tích.\r\n\r\nXa hơn vào năm 2015, diện mạo của Bưu điện Thành phố Hồ Chí Minh cũng trở thành chủ đề bàn tán sôi nổi khi màu sơn của công trình được nhiều người cho rằng là quá rực rỡ. Sau đó nhiều cuộc họp giữa các cơ quan, chuyên gia trong ngành đã được tổ chức để lấy ý kiến, bỏ phiếu lựa màu sơn chọn nhằm \"thay áo\" cho công trình này.\r\n\r\nCòn mới đây tại Hà Nội, căn biệt thự pháp tại số 49 Trần Hưng Đạo trong quá trình tu sửa vào năm ngoái cũng nhận về nhiều lời bàn tán tương tự về màu sắc mới của công trình.\r\n\r\nTuy nhiên, theo các chuyên gia đến từ Pháp và những người sống lâu năm gần căn biệt thự này thì màu sắc này mới chính là diện mạo nguyên bản của căn nhà khi mới được xây dựng.\r\n\r\nNgoài ra, vẻ bề ngoài nhiều di tích khác sau quá trình trùng tu như Nhà Hát Lớn Hà Nội, Nhà Thờ Lớn cũng trở thành chủ đề nóng được bàn tán trong một khoảng thời gian.\r\n\r\nHình ảnh bên ngoài của mỗi góc phố, dãy nhà, công trình lâu năm nào đó đều thường gắn với kí ức của nhiều người, nên yêu là dễ hiểu. Nhưng tình yêu ấy cũng có những nét cực đoan, nó giống như việc ta đặt ra tiêu chí rằng: người mình yêu là tuyệt đối hoàn hảo, họ không được thay đổi một chút xíu nào, họ phải đứng đấy bên ta bất chấp tuổi tác, bệnh tật và thời gian …\r\n\r\nTiêu chuẩn trùng tu\r\n\r\nNhững gì đã được thực hiện từng bước có quy chuẩn rõ ràng thì có lẽ nếu có lạ một chút, nó cũng không đáng bị la. Với các di tích lịch sử, các di sản văn hoá nói chung, việc bảo tồn theo những quy trình rõ ràng lại càng cần thiết bởi đó là một phần của văn hóa, mà \"Văn hoá là bản chất, văn hoá là cội nguồn, văn hoá là dân tộc\".\r\n\r\nNgày nay, với sự sáng tạo của tuổi trẻ và sự phát triển của công nghệ, nhiều cách mới để bảo tồn, lưu giữ, lan tỏa những giá trị mang tính văn hóa cũng đang được áp dụng. Có thể lạ một chút, nhưng thật đáng để chúng ta cổ vũ, khích lệ.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/08/02/package-media-95033965402513304412271-80369811283642550739975.png\">\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/08/02/z5675801162654-084ee5f9b3741bd45cbbf19f4387303f-41120366222805815783827-18516404713185258943989.webp\">\r\n\r\nPhát huy giá trị của các di sản\r\n\r\nBắt đầu từ năm 2022, một loạt video kể chuyện về các di sản công nghiệp của Hà Nội đã được Hanoi Ad Hoc đăng tải. Những địa danh thân thuộc, gắn với bao thế hệ người dân thủ đô như Khu Cao - Xà - Lá; Nhà máy in Tiến Bộ, Nhà máy dệt 8/3… Các video này cho thấy những nghiên cứu sâu và nghiêm túc của nhóm về các yếu tố văn hóa - lịch sử làm nên cảnh quan Hà Nội.\r\n\r\nMột chuyến hải trình mang tên \"Hành trình di sản\" lần đầu tiên được triển khai, kéo dài từ Vịnh Hạ Long tới Vườn quốc gia Bái Tử Long, bắt đầu từ tháng 5/2024. Đây được xem là hành trình xa nhất, lâu nhất trên vịnh, đưa du khách đi khám phá những khu vực ít ai được chiêm ngưỡng của Vịnh Hạ Long.\r\n\r\nĐặc biệt, du thuyền triển khai chuyến hải trình này cũng được đánh giá là du thuyền hiện đại nhưng thân thiện với môi trường, giảm thiểu tối đa những tác động không tốt tới tự nhiên.\r\n\r\nÔng Lương Thế Tuyên - Đại diện thương hiệu Grand Pioneers cho biết: \"Chúng ta bảo tồn thiên nhiên, bảo tồn những di sản này thì cũng chính là bảo vệ chúng ta, giữ gìn những giá trị cho mai sau…\"\r\n\r\nVà còn rất nhiều những dự án ứng dụng công nghệ thực tế ảo đã hồi sinh các di tích lịch sử, giúp du khách ở bất cứ đâu cũng có thể thăm quan các công trình văn hoá, kiến trúc, lịch sử nổi bật. Tất cả đều với mục tiêu phát huy, quảng bá di sản của Việt Nam đến với đông đảo du khách trong và ngoài nước.', 'di tích Chùa Cầu.png', 1, 1, 267, '2024-10-29 05:06:20', '2024-10-29 05:06:20', 5),
(2, 'Quyến rũ hương vị tào phớ Hà Nội xưa', 'Sẽ thật thiếu sót nếu không nhắc đến Tào phớ khi nói về những thức quà dân giã của Hà Nội.', 'Không chỉ là một món ăn quen thuộc, trong tâm thức của nhiều người Hà Nội, tào phớ còn gợi về một miền kí ức với tiếng rao thân quen trong những con ngõ nhỏ vào buổi trưa thanh vắng hay đêm đông rét mướt.\r\n\r\nCái tên \"tào phớ\" được phiên âm từ tên gọi \"to fu\" (tào phở) - một món ăn của người Hoa đã du nhập vào Việt Nam từ rất lâu. Nhờ hương vị dân dã, tốt cho sức khỏe nên dần dà, tào phớ trở thành món ăn quen thuộc của người Việt.\r\n\r\nMỗi miền có một cách thưởng thức tào phớ khác nhau, thế nhưng ở Hà Nội, tào phớ được đánh giá là giữ được hương vị nguyên bản nhất với những lát phớ trắng ngà, thơm mịn, mát lành…khi dùng với nước đường ngọt vừa phải và đượm hương hoa nhài.\r\n\r\nĐơn giản, rẻ tiền nhưng để có một bát tào phớ say lòng thực khách lại đòi hỏi sự cầu kỳ, và cả khéo léo của người bán. Tào phớ truyền thống ở Hà Nội thường đựng trong thùng và luôn được giữ nóng, khách ăn đến đâu hớt ra đến đó. Đặc biệt, để lấy tào phớ, người bán không dùng thìa để xúc ra mà phải gạt từng lớp mỏng bằng vỏ trai hoặc tấm tôn mảnh, to bản.\r\n\r\nTào phớ là một món \"gây thương nhớ\" trong cả những lúc trời lạnh hay nóng, thức quà này cũng có vị ngon riêng.\r\n\r\nĐối với không ít người, một buổi sáng tinh khôi dường như trở nên tuyệt vời hơn khi được thưởng thức một bát tào phớ \"chuẩn\". Đó là những lát tào phớ mỏng manh, trắng ngần được chan với nước đường thơm ngát hương hoa bưởi hoặc hoa nhài tùy theo mùa. Hương thơm ấy quyện hòa cùng không khí trong lành của buổi sớm như tiếp thêm năng lượng cho một ngày mới.\r\n\r\nỞ Hà Nội, làng An Phú (nay thuộc phường Nghĩa Đô, quận Cầu Giấy) được xem là làng nghề \"khai sinh\" ra món tào phớ.\r\n\r\nMỗi gia đình có một bí quyết làm nghề riêng, nhưng tựu chung nguyên liệu chủ yếu là đậu nành ngon, được ngâm qua đêm và đãi sạch vỏ, bỏ hết các hạt hỏng, mốc. Sau đó, người ta nấu thành sữa đậu nành, lọc bỏ bã rồi tiếp tục đun cùng thạch rau câu, lá nếp. Khi hỗn hợp đạt yêu cầu, để nguội, phần óc đậu sẽ đặc quánh và có mùi thơm ngậy của lá nếp hòa với vị béo bùi của đậu nành.\r\n\r\nNgày nay, tào phớ Hà Nội vẫn xuất hiện ở các hàng chè, nhưng đã thay đổi ít nhiều để phục vụ nhu cầu của lớp trẻ khi cho thêm trân châu, hạt sen, cốt dừa hay caramen béo ngậy...\r\n\r\nThực ra, tào phớ không phải món riêng có của Hà Nội, nhưng Hà Nội lại tạo cho món ăn dân dã này những nét riêng có bởi chính sự tinh tế trong cốt cách của người Hà Thành, bởi chính đặc trưng văn hóa nơi đây.', 'tào phớ.png', 1, 0, 119, '2024-10-29 04:27:58', '2024-10-29 04:27:58', 2),
(3, 'Can thiệp y học có phải yếu tố then chốt để cuộc sống người chuyển giới được bình ổn?', 'Can thiệp y học được biết đến như một trong những phương pháp xử lý bức bối giới cho người chuyển giới.\r\n\r\n', 'Can thiệp y học trong quá trình định giới thường bao gồm hormone nội tiết, phẫu thuật ngực, phẫu thuật cơ quan sinh dục. Các phương pháp này có thể áp dụng đơn lẻ hoặc kết hợp dựa vào nhu cầu và mức độ bức bối giới của họ. Khi đưa ra lựa chọn can thiệp y học, người chuyển giới thường phải cân nhắc nhiều yếu tố, trong đó yếu tố được cân nhắc nhiều nhất là chi phí và khả năng đáp ứng về mặt sức khoẻ. Trong quá trình can thiệp, họ phải đối mặt với khó khăn như tác dụng phụ, biến chứng nhưng những khó khăn không chỉ dừng lại ở đó mà còn xuất hiện ở những góc khuất khác trong đời sống.\r\n\r\nAnh M người chuyển giới nam đang sinh sống cùng vợ và con nhỏ ở Hà Nội chia sẻ anh bắt đầu can thiệp y học cách đây hơn 5 năm, việc này góp phần giúp anh tự tin hơn trong đời sống. Tuy nhiên, vì chưa thể thay đổi giới tính trên giấy tờ, anh không được đứng tên trên giấy tờ cho con. Nếu nhận nuôi thì vai trò của anh ở trên giấy tờ sẽ là mẹ nuôi và điều đó khiến anh gặp nhiều khó khăn trong quá trình làm việc với nhà trường. Không chỉ vậy, anh chia sẻ thêm rằng anh luôn gặp khó khăn trong các giao dịch dân sự như làm việc với ngân hàng, thủ tục hành chính do giới tính trên căn cước của anh là nữ nhưng ngoại hình lại là nam. Việc bị hỏi liên tục khiến anh cảm thấy phiền muộn và quyết định không tham gia trực tiếp vào quá trình giao dịch dân sự. Anh trao đổi rằng bản thân chỉ có thẻ căn cước và bằng lái xe, còn lại như mã số thuế hay thẻ bảo hiểm xã hội anh đều không có. Khi được hỏi nếu có điều bất trắc xảy ra với anh, thì vợ và con anh sẽ thế nào? Anh đáp rằng:\r\n\r\n\"Anh không có gì đâu em ạ, có ra đi cũng không sao, tất cả giấy tờ hay tài sản đều đứng tên vợ anh và ngay cả tài sản thừa kế cũng là đứng tên cho con của anh dưới dạng đem tặng rồi\".\r\n\r\n<img src=\"https://cdn-images.vtv.vn/66349b6076cb4dee98746cf1/2024/10/28/a1--4--76061799985604390831623.jpg\">\r\n\r\nMột góc khuất khác, L (21 tuổi) là một người chuyển giới nữ chưa can thiệp y học chia sẻ: \"Khác với mọi người ở Hà Nội thì em là người ở Nam Định nơi mà các kiến thức về người chuyển giới còn hạn chế. Ở môi trường này thì em còn bị quấy rối tình dục bằng nhiều cách như là gạ quan hệ, xúc phạm cơ thể bằng hành động động chạm khi chưa được sự đồng ý của em hoặc dùng lời lẽ thiếu lịch sự để trêu đùa\". Sự phân biệt đối xử và quấy rối mà L gặp phải dù có can thiệp hay không cũng không làm giảm đi những trải nghiệm mang đầy tính tổn thương và ám ảnh với một người chỉ đang trong độ tuổi sinh viên.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/a2-thumb--1--89632346014993277299353.jpg\">\r\n\r\nTrao đổi về vấn đề liên quan đến những kỳ thị, phân biệt đối xử mà người chuyển giới gặp phải, Tiến sĩ Tâm lý Hồ Thu Hà, Giảng viên Trường Đại học Giáo dục, Đại học Quốc gia Hà Nội chia sẻ:\r\n\r\n\"Xử lý bức bối giới bằng can thiệp y học là một quan tâm lớn với người chuyển giới, nhưng không phải là giải pháp toàn diện cho các khó khăn mà họ gặp phải. Thực tế, người chuyển giới phải đối diện với phân biệt đối xử và bạo lực, đặc biệt khi các thông tin danh tính và giới tính trên giấy tờ của họ không trùng khớp với bản sắc giới hay thể hiện giới của họ. Điều này có thể tạo các khoảng trống pháp lý ngăn cản người chuyển giới tiếp cận các quyền cơ bản, và nuôi dưỡng các định kiến và nguy cơ phân biệt đối xử với họ. Do vậy, một trong điều cần thiết để phòng ngừa phân biệt đối xử là các quy trình cho phép chính thức của nhà nước với danh tính giới mong muốn của người chuyển giới. Bên cạnh đó, hiểu rằng định kiến có thể hình thành ngầm ẩn và dễ chuyển thành các kỳ thị và bạo lực với các nhóm không theo chuẩn mực số đông về giới, cần chính sách của nhà nước để ngăn chặn các hành động này; và các chính sách này cần được phổ biến rộng trong cộng đồng. Ở một góc độ khác, một phần lớn định kiến đến từ việc cộng đồng thiếu hiểu biết về đa dạng giới và tính dục, đi cùng với nỗi sợ với người chuyển giới (transphobia). Giáo dục về đa dạng giới và tính dục trong các bối cảnh khác nhau: trường học, công sở, gia đình, v.v. có thể giúp nâng cao hiểu biết và giảm thiểu định kiến hay phân biệt đối xử. Cuối cùng, vì người chuyển giới có các vấn đề riêng về sức khỏe thể chất và tâm thần, hệ thống y tế cần được nâng cao hiểu biết và năng lực chuyên môn để họ được đối xử một cách bình đẳng.\"\r\n\r\nVốn dĩ không có cuộc sống của ai là dễ dàng nhưng dường như những sự khó khăn liên tầng luôn xảy đến với số phận của người chuyển giới. Can thiệp y học là một lựa chọn để họ giải quyết một phần vấn đề của cuộc sống, dù vậy, tùy từng thời điểm mà họ sẽ lựa chọn nhu cầu cần được ưu tiên hơn. Dù vậy, có vẻ với người chuyển giới, nhu cầu có sự đồng hành và được thừa nhận giới tính pháp lý để có một sự bảo vệ trọn vẹn mới là cao nhất. Muốn hiện thực hóa những nhu cầu chính đáng này, sẽ cần sự chung tay của đa ngành, cơ quan, tổ chức và xã hội nhằm tạo ra một không gian bình đẳng, an toàn hướng đến một xã hội thật sự phát triển.', 'vấn nạn quấy rối tinhduc.png', 1, 1, 376, '2024-10-29 05:05:59', '2024-10-29 05:05:59', 4),
(5, 'Bỏ phố vào rừng để giữ đại ngàn xanh edited', 'Giữ rừng bằng mô hình vườn rừng và phủ xanh đất trống đồi trọc bằng \"bom hạt giống\".', '<img src=\"https://cdn-images.vtv.vn/thumb_w/650/66349b6076cb4dee98746cf1/2024/10/27/img-4932-40722279224212927663585-07079080665523103295374.jpg\">\r\n\r\nTôi trở lại Đắk Lắk để tìm hiểu câu chuyện du lịch ngắm voi trong hoang dã ở Vườn quốc gia Yok Đôn. Nếu Yok Đôn ở Buôn Đôn đã triển khai làm du lịch được một thời gian và có lượng khách nhất định, thì Vườn quốc gia Chư Yang Sin ở huyện Lắk và huyện Krông Bông của tỉnh Đắk Lắk lại chưa khai thác du lịch nhiều.\r\n\r\nVườn quốc gia Chư Yang Sin sở hữu nhiều phong cảnh ngoạn mục với 50 dãy núi cao, thấp khác nhau, nhiều sườn dốc, thảm rừng mênh mông cùng đa dạng hệ thống sông suối, thác ghềnh đan xen. Đặc biệt, khu rừng phong phú với 876 loài thực vật, 203 loài chim, 46 loài thú từng được ghi nhận tại đây. Tuy nhiên, nạn săn bắt thú rừng, chặt trộm cây, hay phá rừng làm rẫy vẫn là vấn đề nhức nhối. Trước thực trạng đó, có những con người tha thiết với thiên nhiên đã quyết tâm bỏ phố vào rừng để bảo vệ đại ngàn xanh.\r\n\r\nTrồng vườn rừng, tặng cây giống cho bà con\r\n\r\nĐược sự giới thiệu của một người bạn làm du lịch và thiện nguyện ở Đắk Lắk, tôi tìm đến căn nhà tre của vợ chồng anh Phạm Quang Thái cạnh thác Bìm Bịp. Dòng thác hoang sơ hùng vĩ nằm ở trong cánh rừng đại ngàn Chư Yang Sin thuộc địa phận của buôn Năm Pă, xã Yang Tao, huyện Lắk, cách trung tâm TP. Buôn Ma Thuột 50km theo QL27 hướng Đông Nam.\r\n\r\nSau khi xuống xe ô tô, tôi phải ngồi xe máy thêm một đoạn đường, rồi lại đi bộ lên dốc mới đến được Thác Bìm Bịp. Dù có hơi oải một chút, nhưng cảm giác ấy bỗng tan biến khi trước mắt tôi là dòng thác xối nước mạnh, như thả vào hồ nước phía dưới một dải lụa trắng, như một cô gái đang mặc tà áo dài trắng nhưng đầy nội lực. Ở suối đá, vài bà mẹ đang cho con nhỏ chơi đùa cùng dòng nước mát. Đi vài mét qua con suối, tôi đến nhà anh Thái. Một căn nhà nhỏ bằng tre như hiện ra từ một câu chuyện cổ tích do chính tay anh mua tre về cất dựng. Dù đường lên đây vất vả, dù điều kiện sống bớt tiện nghi, nhưng anh Quang Thái và vợ đã bỏ cuộc sống ở TP. Buôn Ma Thuật để vào đây bám rừng, làm dự án trồng vườn rừng. Anh tâm sự: \"Mình đã đi qua nhiều vùng đất, thấy cảnh rừng bị xâm chiếm và tàn phá dần từ nhiều phía, nhất là phần giáp với đất sản xuất của người dân. Chính những vườn sản xuất độc hại và độc canh ấy cũng gây ảnh hưởng đến sức khỏe của người dân và tương lai chúng ta. Sau nhiều lần suy nghĩ đắn đo và nghiên cứu cách vận hành của rừng tự nhiên, mình đã nghĩ ra mô hình vườn rừng đa tầng tán.\"\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/27/img-8805-04345380739638828590742.jpg\">\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/27/img-8807-63573856632343880113526.jpg\">\r\n\r\nĐể thực hiện giấc mơ phủ xanh đất trống đồi trọc và bảo vệ những cánh rừng còn lại của thiên nhiên, năm 2017, anh Thái lựa chọn huyện Lắk, vùng lưu vực ven thác Bìm Bịp để thực hiện dự án. Khu vực này cũng hội tụ đầy đủ các yếu tố để mô hình phát huy vai trò tốt nhất, như: Có người dân thiểu số, nghèo khó sống cận rừng và phụ thuộc vào rừng; Khí hậu, nguồn nước và thế đất phù hợp với hệ sinh thái vườn rừng đa tầng tán (nhất là đất sạch, nước khoáng tự nhiên từ trên cao chảy xuống); Kết hợp được các hoạt động văn hóa du lịch, quảng bá và lan tỏa mô hình điểm.\r\n\r\nViệc chuyển vào sống gần rừng giúp anh Thái sát sao được với công việc, gần gũi với bà con, đồng thời có thể canh giữ bảo vệ rừng. Căn nhà tre ấy, dù có đơn sơ nhưng lại ấp ủ lý tưởng lớn của cả hai vợ chồng. Theo anh Thái, thay đổi nhận thức và các nguồn lực đầu tư ban đầu luôn là khó khăn lớn nhất trong việc thực hiện mô hình vườn rừng.\r\n\r\n\"Người dân sống dựa vào rừng, mỗi người làm một chỗ cách xa nhau; đa phần không dùng điện thoại và công nghệ; một vài người khó khăn về ngôn ngữ, nên việc gặp và chia sẻ kiến thức, hướng dẫn mô hình khó khăn. Tuy vậy tôi đã bỏ tiền túi để mua cây tặng cho người dân, thường xuyên cùng họ lên nương, thu mua một vài sản phẩm như hạt rừng, dược liệu... vừa làm vừa giải thích dần cho họ hiểu. Tôi cũng mời thêm bà con tham gia các lớp tập huấn chuyên sâu khi họ tham gia mô hình. Sau 2 năm, một số hộ dân bắt đầu thu hoạch sâm dược liệu và cà phê cũng tăng năng suất, được cộng giá thu mua. Những cây trồng lâu năm cũng tạo ra tầng tán và cho thu nhập như ca cao, mắc ca...\" – anh Thái chia sẻ.\r\n\r\nDiện tích thực hiện mô hình ngày càng được mở rộng, các nguồn hỗ trợ cũng ngày một tăng, chủ yếu thông qua du lịch trồng cây. Các loại cây trồng có chất lượng cao hơn và đang dần tạo thành hệ sinh thái rộng lớn. Anh Nguyễn Văn Thìn (SN.1988) cũng là một người dân được truyền cảm hứng từ nỗ lực của anh Thái. Anh Thìn đã dựng một ngôi nhà tre nhỏ để phát triển vườn rừng ở xã Hòa Lễ (huyện Krông Bông), đồng thời suốt 7 năm qua, anh thường xuyên tuần tra rừng, nhiều lần báo cho lực lượng kiểm lâm khi phát hiện lâm tặc xâm phạm.\r\n\r\n\"Ngày xưa ông bà dùng nhiều phân bón hóa học, thuốc trừ sâu nên thoái hóa hết đất. Chết đất, chết môi trường. Thấy nhiều cảnh người thân của mình bị ung thư, bệnh tật nên mình cũng nhìn nhận lại. Bây giờ mình trồng trọt thuận tự nhiên để cải tạo hệ sinh thái. Nếu cứ chặt cây và tàn phá đất thì khi sạt lở, mưa lũ, thiên tai xảy ra, làm bao nhiêu cũng trở về con số 0 hết. Nói chung mình phải hài hòa cả hai thứ, dân thì kiếm ăn được còn rừng tưới tắm cho dân.\" – anh Thìn bộc bạch.\r\n\r\nPhủ xanh đất trống với \"bom hạt giống\"\r\n\r\nNhiều khu rừng, đất trống đồi trọc mất hàng chục năm vẫn không thể tái sinh và phục hồi, đa phần là do người dân liên tục phát dọn, đốt phá. Họ cho rằng các cây gỗ đó không hề mang lại giá trị kinh tế nên xâm lấn rừng để trồng bắp đậu và các cây nông nghiệp. Để dung hòa mối quan hệ giữa đất canh tác lâu đời của người dân bản địa và các khu bảo tồn thiên nhiên, anh Quang Thái đã nghiên cứu nhiều giống cây có giá trị kinh tế dưới tán rừng như: sâm dược liệu, cây ăn trái bản địa, cây lấy hạt... để đưa và \"bom hạt giống\".\r\n\r\n\"Bom hạt giống\" là một ý tưởng sáng tạo của người dân Ấn Độ để khôi phục những khu đất bị sa mạc hóa, đất trống, đồi trọc, trảng cỏ và trồng mới những cánh rừng bị chặt phá. Đây là một phương pháp gieo trồng bằng hạt giống được bọc trong vật liệu đất. Đất bọc hạt thường là hỗn hợp đất sét và các thành phần khác như phân trộn hoặc mùn. \"Bom hạt giống\" sau khi hoàn thành sẽ được phơi khô trước khi gieo. Sau khi mang đi thả trên rừng hoặc vùng đất trống đồi trọc, các hạt giống nằm im trong lớp đất đợi khi có mưa sẽ nảy mầm, cây con mọc lên đã có sẵn một lượng dinh dưỡng đủ để phát triển ban đầu. Mùa mưa kéo dài khoảng 2 – 3 tháng đủ để cây con cứng cáp. Khi mùa khô hạn đến thì sức chống chọi của cây con cũng mạnh hơn.\r\n\r\nBên cạnh đó, giá thành làm ra \"bom hạt\" rẻ hơn rất nhiều lần so với việc đi mua cây giống về trồng, thuận tiện mang đi xa, lên vùng cao để \"quăng bom hạt\". Chúng tái sinh tự nhiên, cắm rễ cọc sâu vào đất nên phù hợp với cảnh quan sinh thái môi trường rừng. Từ đó góp phần phục hồi rừng nhanh hơn, các loài chim, sóc, thú nhỏ cũng có thêm nhiều nguồn dinh dưỡng để sinh sôi nảy nở.\r\n\r\nKhông chỉ phát triển vườn rừng, anh Thái còn ấp ủ giúp người dân làm du lịch cộng đồng để có thêm nguồn thu nhập, dựa vào cảnh quan thiên nhiên và văn hóa truyền thống lâu đời để đón khách.\r\n\r\n\"Hàng tỉ năm qua, chúng ta được rừng bao bọc, nuôi dưỡng và che chở, cớ sao bây giờ núi non hoang tàn. Ngay bây giờ nếu không nghiêm túc nhận thức lại về giá trị của rừng, thì tự chúng ta tước đi quyền được sống, tự do, hạnh phúc của loài người cũng như muôn loài khác. Việc bảo vệ, phục hồi rừng phải được xem là mục tiêu quan trọng nhất của mọi mục tiêu. Những thách thức của tương lai đa phần đến từ việc cạn kiệt và suy thoái nguồn tài nguyên thiên nhiên nên cần có pháp chế và quy trình giám sát nghiêm ngặt hơn, ứng dụng các công nghệ như camera độ phân giải cao, flycam để tuần tra bảo vệ rừng, đánh dấu mã số nhóm cây gỗ lớn. Quy hoạch tách rời và rõ ràng với các vùng sản xuất lâu đời cận rừng của bà con, giúp bà con vùng khó khăn triển khai mô hình canh tác nông nghiệp thuận tự nhiên \"Vườn Rừng\".\r\n\r\nRời khỏi căn nhà tre mộc mạc thân thương và cảnh sắc nên thơ của thác Bìm Bịp, tôi vẫn nhớ về hương vị của cốc cà phê từ hạt dưới tán rừng hoàn toàn thuận tự nhiên, nhớ về bữa ăn giản dị của những trái tim chung nhịp đập với tình yêu thiên nhiên, tình yêu đất nước. Chợt nhớ đến bài thơ \"Lời cầu nguyện của rừng\" của tác giả người Đức, Hannes Tuch, được Bùi Bá dịch sang tiếng Việt vào khoảng năm 1952, khi ông còn là một kỹ sư thuỷ lâm.\r\n\r\n\"Người có biết những đêm đông giá lạnh, ta bốc hơi ấm lửa hun nồng\r\n\r\nNgười có biết, những ngày nắng gắt, ta che tán mát rượi ánh chiều nung,\r\n\r\nNgười có biết, dưới sườn nhà đồ sộ, ta che người dầu dãi nắng mưa chan;\r\n\r\nNgười có biết, trên nếp giường êm ấm, người nương ta an giấc điệp mơ màng\r\n\r\nNgười có biết, kìa con thuyền vượt sóng, ta đưa người du ngoạn khắp năm châu,\r\n\r\nNgười có biết, này chuôi cày xới đất, ta vun cây cho nẩy nở hoa màu\r\n\r\nChính ta đã rước người vào cuộc thế, trong chiếc nôi âu yếm mẹ đưa ru...', 'vợ chồng anh Phạm Quang Thái chuyển vào sống gần rừng để giúp bà con phát triển vườn rừng.png', 1, 0, 133, '2024-10-29 05:13:17', '2024-10-29 05:13:17', 6),
(6, 'Miss Grand International chính thức tước bỏ danh hiệu Á hậu 2', 'Hoa hậu Hòa bình Myanmar bị tước danh hiệu Á hậu 2 do đã có những hành động và phát ngôn không phù hợp và vi phạm một số quy định của chương trình.', 'Cuộc thi Hoa hậu Hòa bình thế giới - Miss Grand International (MGI) 2024 đã khép lại, với vương miện thuộc về đại diện từ Ấn Độ - Rachel Gupta. Các ngôi vị Á hậu lần lượt thuộc về người đẹp đến từ Philippines, Myanmar, France, Brazil. Tuy nhiên, dù kết quả đã được công bố, những ồn ào bên lề sự kiện vẫn chưa lắng xuống.\r\n\r\nThông tin gây xôn xao cho những người ủng hộ chương trình gần đây là Á hậu 2 - Miss Grand Myanmar Thae Su Nyein đã livestream vào ngày 27/10 và tuyên bố: \"Tôi không cần danh hiệu Á hậu 2 - MGI 2024, không cần phải giữ vị trí đó cho tôi. Vì tôi sẽ ở bên cạnh Giám đốc quốc gia của mình, tôi đến để trở thành hoa hậu chứ không phải để đứng hạng 2, hạng 3\". Bên cạnh đó, người đẹp Myanmar cũng không tham gia các hoạt động ngay sau đêm chung kết.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/snapinsta-app-455155739-18006690599627781-3216082858751516945-n-1080-58985151329293839360990.jpg\">\r\n\r\nTrước đó, ông Soe Min Tun - giám đốc quốc gia Miss Grand Myanmar - tỏ ra không hài lòng với kết quả khi Thae Su Nyein chỉ đạt danh hiệu Á hậu 2. Trong cơn giận, ông bất ngờ giật chiếc tiara Thae Su Nyein đang đội, gỡ bỏ sash Á hậu 2 với thái độ đầy bực tức. Hành động này khiến nhiều người chứng kiến không khỏi bất ngờ và nhận xét rằng phản ứng của ông có phần trẻ con. Ngay sau đó, ông đăng lên trang cá nhân dòng chia sẻ: \"Bye forever\" (dịch: Tạm biệt mãi mãi). Điều này làm dấy lên suy đoán rằng Myanmar sẽ rút khỏi Miss Grand International.\r\n\r\nSau đêm Chung kết, Giám đốc quốc gia Miss Grand Myanmar còn livestream bày tỏ bức xúc, cho biết cuộc thi thực tế có những bất công. Ông Nawat cũng ngay lập tức lên tiếng trong buổi họp báo với truyền thông vừa qua: \"Họ nghĩ rằng vương miện Miss Grand International dễ mua vậy sao? 25.000 USD có bao nhiêu đâu? Tôi đang chờ cô ta trả lại vương miện hay là đã mang lên máy bay về nước rồi. Họ còn nợ phí bản quyền nên sẽ sớm có bản quyền mới cho Miss Grand Myanmar\".\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/snapinsta-app-396285114-18394304680021731-6180970160519735335-n-1080-96545024442167575719770.jpg\">\r\n\r\nHiện tại, sau livestream từ chối danh hiệu của Miss Grand Myanmar, trang chủ MGI đã gỡ bỏ tất cả những hình ảnh và thông tin của Thae Su Nyien ra khỏi top 5 chung cuộc vừa qua. Đồng thời, tổ chức này cũng ra thông báo chính thức quyết định thu hồi danh hiệu Á hậu 2 từ Hoa hậu Myanmar vì Thae Sun Nyein đã có những hành vi và hành động không phù hợp với quy định của chương trình.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/snapinsta-app-464981430-3944601719108388-4270486504191958794-n-1080-04125181577575283857144.jpg\">\r\n\r\nNgay sau khi công bố được đưa ra, những fan sắc đẹp trên khắp thế giới đều tỏ ra đồng tình với ban tổ chức Miss Grand vì đã sẵn sàng loại bỏ những cá nhân không tôn trọng danh hiệu và gây ảnh hưởng xấu đến chương trình.', 'hoa hậu Hòa bình Myanmar.png', 2, 1, 734, '2024-10-29 05:19:12', '2024-10-29 05:19:12', 7),
(7, 'Võ sĩ giác đấu II - Huyền thoại điện ảnh sắp trở lại màn ảnh rộng', 'Gladiator (tựa Việt: Võ Sĩ Giác Đấu) của đạo diễn Ridley Scott không chỉ là một bộ phim; đó là một kiệt tác, một huyền thoại điện ảnh đã làm say đắm khán giả trên toàn cầu.', 'Kể từ khi ra mắt vào năm 2000, ảnh hưởng của nó vẫn còn mãi với thời gian. Giờ đây, Ridley Scott trở lại với Võ Sĩ Giác Đấu II cùng Paul Mescal và Pedro Pascal viết tiếp câu chuyện hào hùng về những võ sĩ giác đấu. \r\n\r\nSẵn sàng chứng kiến vinh quang và máu đổ giữa thành Rome, gợi nhớ một người hùng trên màn ảnh từng đứng lên và ngã xuống hai thập kỷ về trước. \r\n\r\nCuộc đời bi tráng của Maximus\r\n\r\n“Võ sĩ giác đấu” lấy bối cảnh thời kỳ Đế chế La Mã dưới triều đại của Hoàng đế Marcus Aurelius. Nhân vật chính trong phim là Maximus Decimus Meridius (Russell Crowe) - một vị tướng tài lập nhiều chiến công và nhận được sự trọng dụng của Hoàng đế.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/gladiator-3-1280x640-76521011614186931621347.jpg\">\r\n\r\nLòng tràn đầy đố kỵ, Commodus (Joaquin Phoenix), con trai của Marcus Aurelius sau khi lên ngôi đã cho giết hại cả gia đình của Maximus. Bản thân vị tướng bị đem đi xử tử. Sau khi trốn thoát, Maximus rơi vào cảnh nô lệ, buộc phải trở thành võ sĩ giác đấu trong cuộc hành trình tìm kiếm công lý và trả thù. \r\n\r\nĐạo diễn Ridley Scott đưa người xem vào những trận chiến khốc liệt, những âm mưu chính trị và đấu trường đẫm máu. Ở đó, Maximus không chỉ đấu tranh để sinh tồn mà còn quyết tâm giành lại danh dự đã mất. Hành trình đó đã vén bức màn hé lộ sự thối nát của quyền lực hoàng gia. \r\n\r\nĐạo diễn tài hoa Ridley Scott và dàn diễn viên thực lực\r\n\r\n\"Võ sĩ giác đấu\" có tất cả mọi yếu tố mà khán giả chờ đợi ở một tác phẩm sử thi. Bối cảnh hoành tráng, hiệu ứng hình ảnh đột phá, cốt truyện hấp dẫn, kết hợp mượt mà các yếu tố hành động, lãng mạn, nhân vật biểu tượng, nhạc phim đáng nhớ. Để có được thành công cả trên mặt trận phòng vé lẫn được lòng giới chuyên môn, \"Võ sĩ giác đấu\" là tinh hoa của cả một đội ngũ phải kể tới như “thuyền trưởng” Ridley Scott, dàn diễn viên đình đám lẫn bộ phận hiệu ứng, âm nhạc… \r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/gladiator-1200-1200-675-675-crop-000000-20373930961742294123821.jpg\">\r\n\r\nMột đế chế La Mã hùng mạnh và đầy biến cố đã được khắc họa chân thật dưới bàn tay tài hoa của đạo diễn Ridley Scott. Điểm nhấn của phim, đấu trường chính là nơi tôn vinh bạo lực, nơi con người và động vật bị ép phải chiến đấu đến chết để giải trí cho đám đông khát máu. Nơi hưng phấn nhất cũng chính là nơi đạo đức suy đồi nhất, con người trở nên tàn bạo nhất. Phim sẽ không thể thành công nếu thiếu đi sự đóng góp của các diễn viên tài năng. Russell Crowe đã xuất sắc đưa Maximus trở thành một nhân vật đa chiều, biến \"Võ sĩ giác đấu\" - bất chấp những set quay hoành tráng - trở thành câu chuyện tinh tế và bi thảm.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/mv5bmjizmjm3njuzmv5bml5banbnxkftztcwnjq0njcxna----v1--65614011074199833704573.jpg\">\r\n\r\nỞ Maximus đồng thời là sự dũng mãnh của một vị tướng, một người cha, người chồng tan vỡ và một chiến binh giàu trắc ẩn. Russell Crowe biến khoảnh khắc trong phim trở thành bất tử, như câu thoại nổi tiếng của Maximus, “Các người chưa giải trí hay sao?” hay chi tiết đầy kiêu hãnh đầu ngẩng cao nói với Commodus: “Tên ta là Maximus Decimus Meridius…” Giải Oscar dành cho Nam chính xuất sắc nhất chính là minh chứng cho vai trò của ngôi sao này trong thành công của \"Võ sĩ giác đấu\".\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/thumb-1920-640498-91813332584243534889308.jpg\">\r\n\r\nĐối trọng của Russell Crowe trên màn ảnh là Joaquin Phoenix cũng không hề kém cạnh khi “biến hình” trở thành một bạo chúa tuyệt vọng tìm kiếm sự công nhận của cha mình. Nam diễn viên mang đến cho Commodus sự pha trộn giữa yếu đuối và tàn nhẫn, cái tôi mong manh của hắn bộc lộ bằng cảm xúc cực đoan, sự giận dữ, ganh ghét và sợ hãi. Commodus của Phoenix như bức họa tương phản của Maximus từ Russell Crowe. Trong khi Maximus đại diện cho danh dự và lòng trung thành, Commodus của Phoenix là định nghĩa của sự độc ác, bất an và khát khao quyền lực.\r\n\r\nCơn mưa giải thưởng và đề cử\r\n\r\n\"Võ sĩ giác đấu\" là một thành công lớn về mặt phê bình và thương mại, thu về hơn 460 triệu đô la tại phòng vé toàn cầu. Bộ phim nhận được cơn mưa giải thưởng bao gồm 5 giải Oscar bao gồm chiến thắng hạng mục Phim hay nhất và Nam diễn viên chính xuất sắc nhất (Russell Crowe), và 12 đề cử Oscar nói chung. Tác phẩm giành được 2 giải Quả cầu vàng cùng 4 giải BAFTA. Võ Sĩ Giác Đấu được ca ngợi như một trong những bộ phim sử thi xuất sắc nhất của điện ảnh hiện đại.\r\n\r\nTrở lại đấu trường sau 24 năm\r\n\r\n\"Võ sĩ giác đấu II\" đánh dấu sự trở lại của Ridley Scott, hứa hẹn tiếp nối những gì còn dang dở, viết tiếp di sản của Maximus.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/unnamed-39-52556379699309053605630.jpg\">\r\n\r\nNhân vật chính trong phim là Lucius, con trai của Lucilla. Ngôi sao của “Normal People” và “Aftersun”, chủ nhân đề cử Oscar, Paul Mescal được chọn vào vai Lucius. Bên cạnh Mescal, một số gương mặt đáng chú ý của bộ phim phải kể tới Pedro Pascal, Denzel Washington hay Joseph Quinn. Hai diễn viên của phần phim gốc Connie Nielsen và Derek Jacobi lần lượt trở lại trong vai Lucilla và nguyên lão Gracchus. \r\n\r\nVới sự trở lại của Ridley Scott cùng đội ngũ, kết hợp những yếu tố kế thừa từ phần trước và cả đổi mới trong kỹ thuật làm phim, Võ sĩ giác đấu II không chỉ đáp ứng sự kỳ vọng mà còn có thể mở ra một chương mới cho dòng phim sử thi. ', 'Gladiator.png', 2, 0, 1704, '2024-10-29 05:35:42', '2024-10-29 05:35:42', 8),
(8, '“APT.” của Rosé vượt qua kỷ lục của BLACKPINK trên Billboard Hot 100.', '\"APT.\" của Rosé - thành viên của nhóm nhạc nữ BLACKPINK - hiện đứng vị trí thứ 8 trên Billboard Hot 100.', '<img src=\"https://cdn-images.vtv.vn/thumb_w/650/66349b6076cb4dee98746cf1/2024/10/29/package-media-79475613733853016371174-78978808391667357165857.png\">\r\n\r\nRosé và ngôi sao nhạc pop Bruno Mars đã lập nên một cột mốc mới cho các nghệ sĩ nữ K-pop trên bảng xếp hạng Billboard của Hoa Kỳ. Sự hợp tác trong ca khúc \"APT.\" của họ hiện đang đứng ở vị trí thứ 8 trên Billboard Hot 100, đánh dấu thứ hạng solo cao nhất của Rosé trong sự nghiệp và lần đầu tiên cô lọt vào Top 10 Hot 100.\r\n\r\nThành tích này cũng vượt qua các kỷ lục trước đó của BLACKPINK. Bài hát nhóm đứng đầu bảng xếp hạng của nhóm là \"Pink Venom\" đã đạt vị trí thứ 22, trong khi các kỷ lục solo bao gồm \"On The Ground\" của Rosé và \"Rockstar\" của Lisa, cả hai đều ở vị trí thứ 70.\r\n\r\nTrước đây, thứ hạng cao nhất của một nữ nghệ sĩ K-pop thuộc về \"Ice Cream\" - ca khúc kết hợp của BLACKPINK với Selena Gomez. Ca khúc này đã đạt vị trí thứ 13 vào năm 2020. Ngoài thành công trên Billboard, \"APT.\" đã lọt vào Bảng xếp hạng đĩa đơn chính thức của Vương quốc Anh ở vị trí số 4.\r\n\r\nKể từ khi phát hành vào ngày 18 tháng 10, \"APT.\" đã tạo nên một hiện tượng toàn cầu, giúp Rosé đứng đầu các bảng xếp hạng âm nhạc tại Hàn Quốc, trở thành nghệ sĩ solo nữ Hàn Quốc đầu tiên đạt vị trí số 1 trên bảng xếp hạng Spotify của Hoa Kỳ và đạt được thành tích cao nhất trong sự nghiệp trên Bảng xếp hạng bài hát hàng ngày hàng đầu của Spotify toàn cầu.', 'APT.png', 2, 0, 945, '2024-10-29 05:42:29', '2024-10-29 05:42:29', 9),
(9, 'Khoe thành tích của con trên mạng xã hội: Không hẳn đã tốt!', 'Theo các chuyên gia, việc khoe thành tích của con công khai trước đông người không hẳn đã tốt, tiềm ẩn hệ lụy không tốt về tâm lý.', 'Lướt mạng xã hội, không khó để bắt gặp những dòng cập nhật, bức ảnh của mọi người chia sẻ về con cái, từ kỷ niệm ngày con vào lớp 1, tuổi mới đến các các hoạt động thú vị trong ngày. Và thời điểm này, khi một năm học sắp kết thúc, mạng xã hội lại tràn ngập các bài viết thành tích, kết quả học tập của con. Việc khoe con xuất phát từ nhu cầu tự nhiên, mong muốn được chia sẻ cùng mọi người niềm vui, hoặc muốn thông qua mạng xã hội lưu giữ một dấu mốc, kỷ niệm hay thành tựu đầu đời của trẻ. Thế nhưng, theo các chuyên gia việc khoe thành tích của con công khai trước đông người không hẳn đã tốt. Trái lại, việc này còn tiềm ẩn nhiều rủi ro và hệ lụy cho tâm lý của trẻ.\r\n\r\nTrong kỷ nguyên của quyền riêng tư, của AI với những mối nguy về Deepfake, nguy cơ bị thu thập dữ liệu khuôn mặt, việc chia sẻ hình ảnh khuôn mặt trẻ cầm bằng khen với đầy đủ thông tin cá nhân, từ tên họ, trường lớp lên mạng tiềm ẩn nguy cơ mất an toàn trên không gian mạng. Theo cơ quan điều tra, 80% trường hợp bị lừa do chính các nhân tự lộ thông tin. Nhiều bậc phụ huynh vô tình làm lộ thông tin qua thói quen lên mạng xã hội đăng ảnh con, cùng các loại giấy tờ, giấy khen.\r\n\r\nViệc đăng thành tích của trẻ lên mạng không chỉ vô tình để lộ thông tin cá nhân mà còn tạo ra áp lực thành tích cho cả người lớn và trẻ em. Từ đây, nó dẫn tới cuộc chạy đua điểm số trong xã hội. Người này thấy con không có điểm bằng bạn thì về nhà la mắng, gây áp lực với con. Người kia muốn thấy con có kết quả học tập hơn lại tính tới chuyện chuyển trường, chuyển lớp vì cho rằng con mình học tập không hiệu quả. Khi cả xã hội chạy theo điểm số, thành tích thì sẽ dẫn đến hệ lụy lâu dài, tình trạng chạy điểm. Việc công khai kết quả học tập của con cũng tiềm ẩn hệ lụy không tốt về tâm lý.\r\n\r\nHiện nay, theo quy định của Bộ Giáo dục và Đào tạo trong các cuộc họp phụ huynh, giáo viên không được phê bình cá nhân học sinh. Điều này nhằm giữ cho trẻ sự riêng tư và tôn trọng, tránh những bình luận tiêu cực nhắm vào trẻ. Ở bậc tiểu học, giáo viên nhận xét để chỉ ra những điểm tốt, điểm chưa tốt cho trẻ. Có thể thấy, tư duy quản lý giáo dục đã từng bước thay đổi theo hướng tránh chạy theo thành tích.\r\n\r\nTuy nhiên, tâm lý xã hội vẫn nặng về điểm số và thành tích. Đây là điều cần thay đổi từ chính những việc làm nhỏ như tránh khoe con trên mạng. Điều quan trọng hơn điểm số là giúp trẻ hiểu được giá trị của sự nỗ lực.', 'vấn nạn khoe thành tích.png', 2, 1, 286, '2024-10-29 05:47:36', '2024-10-29 05:47:36', 10),
(10, 'Đưa văn hóa Việt tới khán giả toàn cầu qua phim hoạt hình', 'Các nhà sản xuất phim hoạt hình Việt đang ấp ủ nhiều dự án với mong muốn kể câu chuyện của người Việt thông qua nét vẽ của người Việt và lan tỏa đến bạn bè năm châu.', 'Kho tàng văn hóa Việt Nam với những truyền thuyết, phong tục tập quán, trò chơi dân gian đều là những nguồn cảm hứng bất tận với những người làm nghệ thuật. Có không ít những bộ phim hoạt hình chuyển thể từ câu chuyện của văn hóa dân gian Việt Nam được đầu tư bài bản với mục tiêu đưa văn hóa Việt tới khán giả quốc tế.\r\n\r\nSau sự thành công của Wolfoo trên sân chơi toàn cầu, Sconnect đang hướng đến việc sản xuất những sản phẩm mang màu sắc văn hóa Việt Nam.\r\n\r\nÔng Tạ Mạnh Hoàng, Nhà sáng lập Sconnect cho biết: \"Không phải đến bây giờ mà ngay từ những ngày đầu thành lập chúng tôi đã luôn đau đáu việc làm thế nào để mang những gì tinh túy nhất của người Việt vào sản phẩm, khẳng định được chỗ đứng trên thị trường hoạt hình quốc tế bằng bản sắc văn hóa Việt. Không nhất thiết phải đưa phong tục tập quán, trang phục hay bối cảnh Việt Nam xuất hiện trên phim mới thể hiện được văn hóa Việt. Tôi cho rằng một sản phẩm được \"make in Vietnam\" 100% khi có sức lan tỏa rộng rãi cũng là một minh chứng quan trọng để bạn bè quốc tế thấy được năng lực, phẩm chất và những kỹ năng của người Việt\".\r\n\r\nSau gần một thập kỷ đưa hoạt hình Việt đến với công chúng quốc tế, Sconnect đã hợp tác với Alpha Studio để sản xuất và công chiếu series hoạt hình đầu tiên được sản xuất bằng công nghệ 3D từ tháng 8/2023 mang tên Trạng Quỳnh Thời Nhí Nhố.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/29/trang-quynh--at-giai-phim-hoat-hinh-viet-nam--uoc-yeu-thich-nhat-43839008059169100344114.jpg\">\r\n\r\nTrạng Quỳnh - nhân vật dân gian nổi tiếng bởi những giai thoại về sự tài trí hơn người, cùng với đó là bối cảnh làng quê Bắc Bộ đặc trưng bởi cây đa, bến nước, sân đình, là sự giao thoa tinh tế giữa những yếu tố thần thoại và cuộc sống đời thường. Phim dự kiến sản xuất 450 tập, hiện đã phát hành hơn 60 tập, trong đó có nhiều tập đặc biệt gắn liền với những lễ hội truyền thống của Việt Nam như Tết Trung thu, Tết Đoan Ngọ hay Tết Ông Táo.\r\n\r\nBên cạnh việc phát hành phiên bản trên các nền tảng số, các nhà sản xuất cũng đã có kế hoạch sản xuất bộ phim màn ảnh rộng. Bộ phim đã khẳng định được năng lực sản xuất của đội ngũ làm hoạt hình Việt có thể chuyển thể những nội dung thuần Việt lên màn ảnh để phục vụ khán giả trong nước và quốc tế.\r\n\r\nHoạt hình Stopmotion lấy cảm hứng từ những nghệ nhân truyền thống\r\n\r\nViệt Nam hiện có khoảng 5.400 làng nghề truyền thống, trong đó có tới 1.700 làng nghề được đánh giá cao về giá trị văn hóa và tính nghệ thuật.\r\n\r\nKhác với hầu hết các công nghệ hoạt hình đều được thực hiện trên máy tính, đặc thù của hoạt hình Stopmotion (hoạt hình tĩnh vật) là những thước phim được tạo nên từ hàng nghìn bức ảnh nên toàn bộ bối cảnh, đạo cụ đến nhân vật đều phải được làm thủ công. Công nghệ này trở thành mảnh đất màu mỡ để khai thác tối đa khả năng sáng tạo và sự tỉ mỉ - thế mạnh của nguồn nhân lực Việt Nam.\r\n\r\nÔng Nguyễn Ánh Tùng - Giám đốc Sconnect Studio cho biết: \"Khâm phục đôi bàn tay tài hoa của những nghệ nhân làm tò he, chúng tôi tìm đến họ để học hỏi cách tạo hình. Tuy nhiên, bắt tay vào làm mới biết bên cạnh tạo hình còn rất nhiều yếu tố quan trọng khác. Để đáp ứng được các yêu cầu của việc làm phim, chúng tôi đã phải nghiên cứu và cải tiến từ khung xương nhân vật, chất liệu đến công cụ hỗ trợ, làm sao để các cảnh phim được sinh động, quá trình làm phim được rút ngắn.\"\r\n\r\nDù đã có lịch sử lâu đời nhưng hoạt hình Stopmotion vẫn cho thấy tiềm năng dồi dào để khai thác và phát triển, đặc biệt tại một thị trường còn mới mẻ như Việt Nam. Sconnect cho biết dự kiến sẽ cho ra mắt phim hoạt hình Stopmotion chiếu rạp đầu tiên tại Việt Nam trong tương lai gần.', 'Trạng Quỳnh thời nhí nhố.png', 2, 0, 58, '2024-10-29 05:50:33', '2024-10-29 05:50:33', 4);
INSERT INTO `items` (`id`, `title`, `excerpt`, `content`, `image`, `category`, `featured`, `views`, `created_at`, `updated_at`, `author`) VALUES
(11, 'Miền Bắc trời lạnh, miền Trung tiếp tục mưa lớn diện rộng', 'Do ảnh hưởng của không khí lạnh, sáng sớm nay (29/10), hầu khắp miền Bắc trời lạnh. Trong khi đó ở miền Trung, mưa lớn vẫn tiếp diễn.', 'Do ảnh hưởng của không khí lạnh, sáng sớm nay hầu khắp miền Bắc vẫn khá lạnh. Thủ đô Hà Nội và một số nơi khác ở đồng bằng như Hải Phòng, Nam Định chỉ khoảng 19 - 22 độ C. Thậm chí, vùng núi cao như Sa Pa, Tam Đảo giảm thấp chỉ 15 - 16 độ C.\r\n\r\nDự báo đến trưa chiều nay, trời vẫn khá nhiều mây nên nhiệt độ không tăng nhiều. Thủ đô Hà Nội và các thành phố Ninh Bình, Hải Phòng, Lạng Sơn, Hà Giang sang đến Lào Cai, Sơn La, Điện Biên Phủ hầu hết dưới 28 độ C.\r\n\r\nTrong khi đó ở miền Trung, mưa lớn vẫn tiếp diễn từ phía Nam Hà Tĩnh trở vào đến Thừa Thiên Huế. Ngoài ra, lưu ý lũ ở Quảng Bình vẫn đang dao động ở mức cao nên ngập lụt tiếp diễn các huyện Lệ Thủy, Quảng Ninh và thành phố Đồng Hới.\r\n\r\nTheo Trung tâm Dự báo khí tượng thủy văn Quốc gia, trong 24 giờ qua (từ 1h ngày 28/10 đến 1h ngày 29/10), khu vực từ Nam Hà Tĩnh đến Thừa Thiên - Huế đã có mưa to, có nơi mưa rất to như: Hồ Kim Sơn (Hà Tĩnh) 261 mm; Lâm Thủy (Quảng Bình) 346,4 mm; Tà Long (Quảng Trị) 238,8 mm; Hồng Trung (Thừa Thiên - Huế) 152,8 mm. Mô hình độ ẩm đất cho thấy một số khu vực thuộc các tỉnh đã gần bão hòa (trên 85%) hoặc đạt trạng thái bão hòa.\r\n\r\nCơ quan khí tượng thủy văn cảnh báo, nguy cơ xảy ra lũ quét trên các sông, suối nhỏ, sạt lở đất trên sườn dốc tại nhiều huyện: Cẩm Xuyên, Hương Khê, Hương Sơn, Kỳ Anh, thị xã Kỳ Anh (Hà Tĩnh); Bố Trạch, Lệ Thủy, Minh Hóa, Quảng Ninh, Quảng Trạch, thành phố Đồng Hới, Tuyên Hóa, thị xã Ba Đồn (Quảng Bình); Cam Lộ, Đa Krông, Gio Linh, Hải Lăng, Hướng Hóa, thành phố Đông Hà, Triệu Phong, thị xã Quảng Trị, Vĩnh Linh (Quảng Trị); A Lưới, Phong Điền, Phú Lộc (Thừa Thiên - Huế).\r\n\r\nĐộ rủi ro thiên tai do lũ quét, sạt lở đất, sụt lún đất do mưa lũ hoặc dòng chảy cấp 1; tại các tỉnh Quảng Bình và Quảng Trị là cấp 2.\r\n\r\nLũ quét, sạt lở, sụt lún đất do mưa lũ hoặc dòng chảy có thể gây tác động rất xấu đến môi trường, uy hiếp tính mạng của người dân; gây tắc nghẽn giao thông cục bộ, làm ảnh hưởng tới quá trình di chuyển của các phương tiện; phá hủy các công trình dân sinh, kinh tế gây thiệt hại cho các hoạt động sản xuất, hoạt động kinh tế - xã hội. Cơ quan chức năng tại địa phương cần lưu ý rà soát các điểm nghẽn dòng, các vị trí xung yếu trên địa bàn để có biện pháp phòng tránh, ứng phó.\r\n\r\nKhu vực Tây Nguyên hôm nay mưa giảm, chỉ còn xảy ra vài nơi. Các thành phố Kon Tum, Pleiku, Buôn Ma Thuột nhiệt độ phổ biến 26 - 27 độ C. Riêng Đà Lạt nhiệt độ thấp hơn, chỉ 22 độ C.\r\n\r\nNam Bộ tiếp tục có mưa dông rải rác về chiều, cục bộ có điểm mưa to. Còn trước đó sáng và trưa nay, trời có nắng gián đoạn. Nhiệt độ cao nhất ở TP Hồ Chí Minh, Cần Thơ và các nơi khác phổ biến 29 - 32 độ C.\r\n\r\n\r\nTrên biển, ngày và đêm 29/10, vịnh Bắc Bộ, vùng biển phía Bắc khu vực Bắc Biển Đông có gió Đông Bắc cấp 6 - 7, giật cấp 8 - 9; sóng biển cao 2 - 4,5 m; biển động mạnh. Vùng biển từ Khánh Hòa đến Ninh Thuận, vùng biển phía Tây khu vực giữa Biển Đông có gió Tây Nam cấp 6, giật cấp 7 - 8; sóng biển cao 2 - 4m; biển động. Vùng biển Quảng Trị và từ Bình Thuận đến Cà Mau, vùng biển phía Tây khu vực Nam Biển Đông (bao gồm cả vùng biển phía Tây Trường Sa) có gió Tây Nam cấp 5, có lúc cấp 6, giật cấp 7 - 8; sóng biển cao 2 - 3 m; biển động.\r\n\r\nNgoài ra, ngày và đêm 29/10 ở vịnh Bắc Bộ, vùng biển phía Tây khu vực Bắc và giữa Biển Đông (bao gồm vùng biển Hoàng Sa); vùng biển từ Quảng Trị đến Cà Mau, Cà Mau đến Kiên Giang và vịnh Thái Lan có mưa rào và dông; trong mưa dông có khả năng xảy ra lốc xoáy và gió giật mạnh.\r\n\r\nToàn bộ tàu thuyền hoạt động trên các khu vực trên đều có nguy cơ cao chịu tác động của lốc xoáy, gió mạnh và sóng lớn.\r\n\r\nDự báo thời tiết các khu vực ngày và đêm 29/10:\r\n\r\nThủ đô Hà Nội nhiều mây, sáng có mưa, mưa rào rải rác; sau có mưa vài nơi. Gió Đông Bắc đến Bắc cấp 2 - 3; sáng sớm và đêm trời lạnh. Nhiệt độ thấp nhất từ 20 - 22 độ C. Nhiệt độ cao nhất từ 25 - 27 độ C.\r\n\r\nPhía Tây Bắc Bộ nhiều mây, có mưa vài nơi; riêng khu Tây Bắc trưa chiều giảm mây trời nắng. Sáng sớm và đêm trời rét. Nhiệt độ thấp nhất từ 17 - 20 độ C, có nơi dưới 17 độ C. Nhiệt độ cao nhất từ 25 - 28 độ C, có nơi trên 28 độ C.\r\n\r\nPhía Đông Bắc Bộ nhiều mây, có mưa vài nơi; riêng khu vực nam đồng bằng và ven biển ngày có mưa, mưa rào rải rác và có nơi có dông. Gió Đông Bắc đến Bắc cấp 3, vùng ven biển cấp 4. Sáng sớm và đêm trời lạnh, vùng núi trời rét. Nhiệt độ thấp nhất từ 18 - 21 độ C, vùng núi có nơi dưới 17 độ C. Nhiệt độ cao nhất từ 23 - 26 độ C, có nơi trên 26 độ C.\r\n\r\nCác tỉnh từ Thanh Hóa đến Thừa Thiên - Huế nhiều mây, phía Bắc (Thanh Hóa - Bắc Hà Tĩnh) có mưa, mưa rào rải rác và có nơi có dông; phía Nam có mưa vừa, mưa to, cục bộ có nơi mưa rất to và dông. Gió Bắc đến Tây Bắc cấp 3 - 4, vùng ven biển có nơi gió giật cấp 6. Sáng sớm và đêm trời lạnh. Nhiệt độ thấp nhất từ 18 - 21 độ C. Nhiệt độ cao nhất từ 23 - 26 độ C.\r\n\r\nCác tỉnh, thành phố từ Đà Nẵng đến Bình Thuận nhiều mây, có mưa rào và dông rải rác, mưa tập trung vào chiều và tối. Nhiệt độ thấp nhất từ 22 - 25 độ C. Nhiệt độ cao nhất từ 28 - 31 độ C.\r\n\r\nKhu vực Tây Nguyên nhiều mây, có mưa rào và dông vài nơi; riêng phía Nam có mưa rào và dông rải rác, mưa tập trung vào chiều và tối; trong mưa dông có khả năng xảy ra lốc, sét và gió giật mạnh. Nhiệt độ thấp nhất từ 17 - 20 độ C. Nhiệt độ cao nhất từ 24 - 27 độ C.\r\n\r\nKhu vực Nam Bộ nhiều mây, có mưa rào và dông rải rác, cục bộ có nơi mưa to, mưa tập trung vào chiều và tối; trong mưa dông có khả năng xảy ra lốc, sét và gió giật mạnh. Nhiệt độ thấp nhất từ 23 - 26 độ C. Nhiệt độ cao nhất từ 28 - 31 độ C, có nơi trên 31 độ C.', 'ảnh hưởng của không khí lạnh, Bắc Bộ trời lạnh, có nơi dưới 17 độ C.png', 3, 0, 649, '2024-10-29 06:28:09', '2024-10-29 06:28:09', 11),
(12, 'Bờ biển Thừa Thiên Huế sạt lở nghiêm trọng, lớp nền vỉa hè bị “xé toạc” sau bão số 6', 'Triều cường dâng cao, nước biển tràn vào bờ do ảnh hưởng của bão số 6 khiến nhiều đoạn bờ biển Thừa Thiên Huế sạt lở nghiêm trọng.', 'Ảnh hưởng của bão số 6 (Trami), vùng biển Thừa Thiên - Huế có gió giật mạnh nhất cấp 8 - 9, triều cường dâng cao gây sạt lở nhiều đoạn bờ biển.\r\n\r\nĐặc biệt, nước biển tràn vào bờ ở bãi biển Thuận An (thành phố Huế) và xã Phú Thuận (huyện Phú Vang), để lại hậu quả nặng nề cho người dân ven biển.\r\n\r\nDo chưa có kè chống sạt lở, đoạn bờ biển Thuận An - Phú Thuận luôn phải đối mặt với tình trạng sạt lở nặng, ăn sâu vào đất liền qua nhiều đợt thiên tai. Bão số 6 càng khiến tình trạng này trở nên nghiêm trọng.\r\n\r\nTrước khi bão số 6 đổ bộ, tỉnh Thừa Thiên - Huế đã huy động lực lượng vũ trang gia cố bờ kè, sử dụng 4.150 m2 vải lọc, 700 m3 đá hộc, 5.000 bao tải và 200 cừ tràm xử lý khẩn cấp chống sạt lở đoạn qua bờ biển Thuận An - Phú Thuận. Đến nay, phần gia cố này gần như đã bị phá hủy hoàn toàn do bão.\r\n\r\nLớp nền vỉa hè, bê tông bị sóng biển đánh sạt lở nghiêm trọng nằm ngổn ngang. Triều cường dâng cao mang lượng cát lớn tràn vào nhiều hàng quán dọc bờ biển.\r\n\r\nRiêng đoạn bờ biển thuộc xã Phú Thuận đã bị sạt lở thêm khoảng 200 m so với thời điểm trước bão. Một tuyến đường nội bộ của xã bị hư hỏng nặng, 10 hộ dân kinh doanh khu vực bãi tắm bị ảnh hưởng.\r\n\r\nÔng Nguyễn Quang Dân, Chủ tịch UBND xã Phú Thuận cho biết, do tình trạng sạt lở nghiêm trọng nên trong ngày 23/10, UBND tỉnh Thừa Thiên Huế đã công bố tình huống khẩn cấp về thiên tai sạt lở bờ biển đoạn giáp ranh giữa xã Phú Thuận, huyện Phú Vang và phường Thuận An, TP Huế với chiều dài sạt lở gần 1.000 m, trong đó sạt lở nặng 300 m. Những ngày qua, lực lượng Công an, Quân đội, Bộ đội Biên phòng tỉnh Thừa Thiên Huế cùng người dân địa phương đã ra sức đắp kè tạm để chống sạt lở bờ biển ở khu vực này.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/2024-10-28kiemtra5-90023097948808772621724.jpg\">\r\n\r\nLà một hộ dân kinh doanh 30 năm tại bãi biển Thuận An, bà Huỳnh Thị Thứ bàng hoàng cho hay, bà không tưởng tượng được khung cảnh tan hoang sau khi cơn bão đi qua. Trước mắt, cả gia đình bà đang di chuyển cây cối bị gãy đổ và lượng lớn bê tông, gạch, đá hộc bị sóng biển đánh ra khỏi hàng quán để tiếp tục kinh doanh.\r\n\r\n\"Tình hình sạt lở năm nay diễn biến hết sức phức tạp. Một số hộ dân khu vực bãi tắm bị cát bồi lấp, nhà cửa bị tốc mái, cây cối đổ ngã. Chính quyền địa phương gặp khó khăn trong việc khắc phục thiệt hại sau bão do kinh phí lớn, vượt quá khả năng của xã. Địa phương đang triển khai cắt tỉa cây, hỗ trợ bà con di dời, dọn dẹp cát, đá trong nhà, cơ sở kinh doanh nhằm sớm ổn định công việc sau bão\", ông Nguyễn Quang Dân cho biết thêm.\r\n\r\nNgoài bờ biển Thuận An - Phú Thuận, nhiều đoạn bờ biển của 2 huyện Phú Lộc, Phong Điền cũng bị sạt lở.\r\n\r\nTheo thống kê ban đầu của Ban Chỉ huy Phòng, chống thiên tai và Tìm kiếm cứu nạn tỉnh Thừa Thiên - Huế, bờ biển Giang Hải - Vinh Hiền (huyện Phú Lộc) tại vị trí đoạn kè mềm bằng ống cát, nước biển dâng cao, tràn qua nhiều vị trí vào sâu trong khu vực nuôi trồng và sản xuất.\r\n\r\nBờ biển Giang Hải - Vinh Mỹ tiếp tục bị xâm thực, xói lở dài 500 m; đường Trịnh Tố Tâm, khu vực Mũi Chùa (huyện Phú Lộc) sạt lở khoảng 100 m. Bờ biển tại xã Phong Hải, huyện Phong Điền đã bị xâm thực với chiều dài khoảng 1,3 km, chiều sâu 8 m.\r\n\r\nKiểm tra thực tế các điểm sạt lở, Chủ tịch UBND tỉnh Thừa Thiên Huế Nguyễn Văn Phương ghi nhận, đánh giá cao tinh thần chủ động, sẵn sàng ứng phó thiên tai của các lực lượng, người dân nhằm hạn chế việc ảnh hưởng nước biển xâm thực.\r\n\r\nChủ tịch UBND tỉnh Nguyễn Văn Phương yêu cầu các địa phương cử cán bộ theo dõi chặt chẽ diễn biến khu vực sạt lở, kịp thời báo cáo cấp có thẩm quyền có phương án xử lý; đồng thời tổ chức rà soát các khu vực trọng điểm, xung yếu, xử lý kịp thời các sự cố công trình ngay từ đầu nhằm giảm thiểu đến mức thấp nhất nước biển xâm thực.\r\n\r\nChủ tịch UBND tỉnh Nguyễn Văn Phương giao UBND huyện Phú Vang, UBND thành phố Huế và UBND huyện Phú Lộc nghiên cứu, có giải pháp đồng bộ, tổng thể đối với việc phòng, chống sạt lở; chủ động tổ chức di dời, sơ tán người dân ra khỏi các khu vực nguy hiểm, kiên quyết không để người dân ở lại khu vực nguy hiểm.', 'Tuyến đường nội bộ ven biển ở bãi tắm Phú Thuận bị sóng biển đánh hư hỏng.png', 3, 1, 1619, '2024-10-29 06:35:53', '2024-10-29 06:35:53', 4),
(13, 'Hơn 14 triệu công dân đã tích hợp sổ sức khỏe điện tử trên VNeID', 'Khi có sổ sức khỏe điện tử VNeID, người dân không cần phải mua sổ y bạ, các kết quả xét nghiệm, giấy chuyển tuyến... giúp tiết kiệm nhiều chi phí.', 'Bộ Công an cùng Bộ Y tế đang mở rộng triển khai sổ sức khỏe điện tử trên ứng dụng VNeID. Hiện đã có hơn 32 triệu dữ liệu sổ sức khỏe điện tử được tạo lập. Trong đó, hơn 14,6 triệu công dân đã tích hợp sổ trên VNeID.\r\n\r\nVới việc tích hợp này, dữ liệu của hơn 98% các cơ sở khám, chữa bệnh bảo hiểm y tế đã được đồng bộ liên thông qua bảo hiểm xã hội để tích hợp vào VNeID, qua đó tạo thuận lợi cho người dân trong quá trình khám chữa bệnh, cũng như đổi mới công tác quản lý ngành y tế.\r\n\r\nTheo lịch hẹn trước, chị Tâm (quận Long Biên, Hà Nội) đến bệnh viện để kiểm tra tình trạng sức khỏe của mình. Qua ứng dụng VNeID, chị thực hiện thủ tục đăng ký khám nhanh chóng, không cần phải mang theo hồ sơ bệnh án cũ. Bởi trên ứng dụng đã có sẵn thông tin từ những lần khám trước của chị. Qua đó, bác sĩ có thể nắm rõ được quá trình điều trị của bệnh nhân.\r\n\r\n\"Khi sử dụng ứng dụng VNeID này, tôi không cần phải mang hồ sơ lịch sử khám của các đơn vị khác mà mình đã từng khám. Rất là tiện lợi\", chị Phạm Thanh Tâm chia sẻ.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/29/so-suc-khoe-dien-tu-vneid-27837878716049881786312.png\">\r\n\r\nKhi có sổ sức khỏe điện tử VNeID, người dân không cần phải mua sổ y bạ, các kết quả xét nghiệm, giấy chuyển tuyến, hẹn tái khám cũng không cần phải in ra, lưu trữ, giúp tiết kiệm nhiều chi phí. Sau mỗi lần khám, chữa bệnh, hồ sơ bệnh án sẽ được các cơ sở y tế liên thông với cổng tiếp nhận dữ liệu giám định bảo hiểm y tế để tiếp tục cập nhật trên Sổ.\r\n\r\n\"Các dữ liệu về khám chữa bệnh bao giờ cũng phải đảm bảo về tính chính xác, đem lại hiệu quả về quản lý kinh tế, nhân lực, quản lý bệnh viện một cách tốt hơn\", TS. Bác sĩ Lương Đức Dũng (Trưởng phòng Kế hoạch Tổng hợp, Bệnh viện Đa khoa Xanh Pôn, Hà Nội) cho biết.\r\n\r\nTới đây, Bộ Công an, Bộ Y tế và các địa phương sẽ thực hiện đồng bộ nhiều giải pháp: phổ biến đến người dân, liên thông dữ liệu, mở rộng trường thông tin… để việc triển khai sổ sức khỏe điện tử trên VNeID thực sự đem lại hiệu quả thiết thực.\r\n\r\n\"Hiện nay chúng ta mới chỉ triển khai thí điểm trong khối cơ sở khám chữa bệnh có bảo hiểm y tế. Tiến tới sau khi có đầy đủ hành lang pháp lý, chúng tôi sẽ mở rộng ra các cơ sở y tế chưa đăng ký với cơ quan bảo hiểm và những người không có thẻ bảo hiểm y tế. Dữ liệu của những đối tượng này cũng cần phải liên thông vào thì chúng ta mới có cơ sở dữ liệu thống nhất trên toàn quốc\", TS. Bác sĩ Hà Anh Đức (Cục trưởng Cục Quản lý Khám chữa bệnh, Bộ Y tế) cho hay.\r\n\r\n\"Chúng tôi sẽ cùng với Bộ Y tế thúc đẩy các địa phương, các bệnh viện phải sử dụng. Các giá trị đem lại khi ứng dụng này được các bác sĩ sử dụng, công dân sử dụng và lợi ích người dân sẽ được thụ hưởng\", Thượng tá Nguyễn Thành Vĩnh (Giám đốc Trung tâm Dữ liệu Quốc gia về dân cư, C06, Bộ Công an) thông tin.\r\n\r\nTheo tính toán, sổ sức khỏe điện tử VNeID được triển khai rộng rãi sẽ giúp tiết kiệm hơn 1.150 tỷ đồng/năm chi phí mua sổ y bạ. Mục tiêu đến năm sau, mỗi người dân Việt Nam, kể cả người chưa có thẻ bảo hiểm y tế đều sở hữu một sổ sức khỏe điện tử trên ứng dụng VNeID và 1/3 người dân khi đi khám chữa bệnh có bệnh án điện tử.', 'VNeID.png', 3, 1, 1257, '2024-10-29 06:47:23', '2024-10-29 06:47:23', 12),
(14, 'Lũ ở Quảng Bình đặc biệt lớn, nhiều nhà ngập tới nóc', 'Theo dự báo, lũ tại Quảng Bình còn duy trì ở mức cao trên báo động 3 trong ngày hôm nay (29/10), ngập lụt còn kéo dài ít nhất 2 - 3 ngày tới.', '<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/hon-17-000-ngoi-nha-o-tinh-quang-binh-bi-ngap-do-lu-lut-anh-tran-long-51452812273156506694397.jpg\">\r\n\r\nDo mưa lớn liên tục, lũ ở Quảng Bình đêm qua (28/10) đặc biệt lớn, hiện mực nước ở sông Kiến Giang vẫn đang dao động ở mức đỉnh, chỉ kém mức lũ lịch sử năm 2020 khoảng 74 cm. Tình trạng ngập lụt sâu diện rộng còn kéo dài.\r\n\r\nNguyên nhân do mưa rất lớn trong vòng 2 ngày qua, lượng mưa đo được ở Lệ Thủy, Quảng Bình đã lên tới 848 mm. Lượng nước lớn đổ về sông Kiến Giang khiến lũ lên rất nhanh.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/cong-an-huyen-le-thuy-ho-tro-nguoi-dan-trong-lu-20841692390788027647678.jpg\">\r\n\r\nLũ dâng cao lại thoát chậm do địa hình cửa sông Nhật Lệ đổ ra biển hẹp hơn so với trong lòng sông, kết hợp với thủy triều như bức tường chắn khiến nước lũ bị giữ trong sông gây ngập sâu diện rộng.\r\n\r\nĐến 1h đêm, mực nước ở trạm Lệ Thủy đã lên 4,14m, vượt báo động 3 khoảng 1,44 m. Nhiều nhà mái ngói đã ngập cả nóc nhà.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/nguoi-dan-di-chuyen-bang-do-trong-lu-06436284511446183981420.jpg\">\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/pho-bien-thanh-song-nguoi-dan-luu-thong-bang-do-51215486517653826496109.jpg\">\r\n\r\nDự báo lũ còn duy trì ở mức cao trên báo động 3 trong ngày hôm nay (29/10), ngập lụt còn kéo dài ít nhất 2 - 3 ngày tới. Khoanh vùng ngập sâu nhất là khu vực huyện Quảng Ninh và Lệ Thủy và thành phố Đồng Hới. Người dân ở các vùng trũng thấp cần tiếp tục sơ tán để đảm bảo an toàn.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/nguoi-dan-len-cac-nha-cao-tang-tranh-lu-37963619451599201181318.jpg\">\r\n\r\nThượng tá Trần Anh Tuấn (Trưởng Công an huyện Lệ Thủy) cho biết, mưa lớn liên tục làm nước lũ lên nhanh, người dân nhiều nơi kêu cứu. Chính quyền địa phương cùng Công an huyện và lực lượng tại chỗ đã có mặt kịp thời giúp đỡ bà con.\r\n\r\nTheo Thượng tá Trần Anh Tuấn, hiện Công an huyện Lệ Thủy huy động 100% quân số sẵn sàng giúp người dân di dời, ứng phó tình huống khẩn cấp.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/vung-ron-lu-le-thuy-ngap-sau-anh-tran-long-61471621509971103310435.jpg\">\r\n\r\n\"Trong đêm tối, nhiều người dân ở Lệ Thủy bằng các phương thức như gọi điện, lên mạng xã hội cầu cứu, mong được hỗ trợ giúp đỡ di dời khẩn cấp khi nước lũ lên nhanh. Các tổ nhóm xung kích Công an huyện Lệ Thủy đã trắng đêm giúp dân chống lũ. Đối với các vị trí cô lập, chúng tôi chỉ đạo công an cơ sở và các tổ nhóm phản ứng nhanh kịp thời có mặt hỗ trợ nhu yếu phẩm để bà con yên tâm\", Thượng tá Trần Anh Tuấn thông tin.', 'Vùng rốn lũ Lệ Thủy ngập sâu.png', 3, 0, 655, '2024-10-29 07:07:59', '2024-10-29 07:07:59', 10),
(15, 'Mưa lũ ở Quảng Bình làm 1 người chết, 3 người mất tích, hơn 32.700 nhà bị ngập sâu', 'Theo thống kê, đến thời điểm này, mưa lũ tại tỉnh Quảng Bình đã làm 3 người mất tích, 1 người chết, hơn 32.700 nhà dân bị ngập lụt, 58 thôn, bản bị chia cắt...', 'Tỉnh Quảng Bình vẫn mưa lớn do ảnh hưởng bão số 6 khiến số nhà dân bị ngập nặng tiếp tục tăng. Đến thời điểm này, mưa lũ tại tỉnh Quảng Bình đã làm 3 người mất tích, 1 người chết, toàn tỉnh có 32.767 nhà dân bị ngập lụt, 58 thôn, bản bị chia cắt; các tuyến đường giao thông bị ngập tại 76 điểm, sạt lở 13 điểm, 3 tàu cá bị chìm, sạt lở 1,5km kè biển.\r\n\r\nTrong đó, huyện Lệ Thủy có hơn 19.760 nhà, huyện Quảng Ninh hơn 12.000 nhà và thành phố Đồng Hới 1.000 nhà dân bị ngập sâu. Tỉnh Quảng Bình đã di dời 1.249 hộ với 3.681 nhân khẩu; trong đó huyện Quảng Ninh phải di dời nhiều nhất là 1.105 hộ với 3.125 khẩu. Các địa phương cũng tiến hành di dời tại chỗ 9.123 hộ dân.\r\n\r\nNgoài ra, toàn tỉnh có 76 điểm bị ngập trên các tuyến đường giao thông. Trong đó, tuyến Quốc lộ có 14 điểm bị ngập chủ yếu tại Quốc lộ 9B, Quốc lộ 15, Quốc lộ 9C, Quốc lộ 9E và Quốc lộ 1; đường Tỉnh lộ có 62 điểm bị ngập tại thành phố Đồng Hới, Bố Trạch, Minh Hóa và Quảng Trạch. Bên cạnh đó, có 15 điểm sạt lở tại các tuyến Quốc lộ và tại huyện, thị.\r\n\r\nĐược biết, khoảng 8h ngày 28/10, ông Ngô Du Lịch (44 tuổi, ở tổ dân phố 1, phường Nam Lý, thành phố Đồng Hới) đi thả lưới đánh cá trên cánh đồng bị ngập lụt ở phía trước nhà.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/29/img-8895-46902816513456516654520-38399231104289448308651.jpg\">\r\n\r\nĐến tối cùng ngày vẫn chưa thấy ông Lịch về nên gia đình, hàng xóm đi tìm và báo chính quyền địa phương. Ngay trong đêm, lực lượng phòng chống thiên tai, tìm kiếm cứu nạn phường Nam Lý đã tổ chức tìm kiếm. Tuy nhiên đến sáng ngày 29/10, lực lượng cứu họ vẫn chưa tìm thấy nạn nhân.\r\n\r\n\r\nCũng trong ngày 28/10, ông Nguyễn Văn Bằng (39 tuổi, ở thôn Xuân Bồ, xã Xuân Thủy, huyện Lệ Thủy) đang trên đường đi đón cháu, sau đó mất tích vào thời điểm nước lũ dâng cao.\r\n\r\nNhận được tin báo, chính quyền địa phương, người dân và các lực lượng chức năng khẩn trương tổ chức tìm kiếm. Tuy nhiên, đến nay lực lượng chức năng vẫn chưa tìm thấy nạn nhân.\r\n\r\nTrưa 28/10, ông Phạm Văn Cứ (64 tuổi, ở xã Gia Ninh, huyện Quảng Ninh) chuẩn bị đò để đi chở đồ nhưng không may gặp luồng nước chảy xiết. Nước lũ đã đưa cả người lẫn đò ra xa bờ, sau đó chiếc đò bị lật, ông Cứ mất tích. Chính quyền xã Gia Ninh, huyện Quảng Ninh cùng các lực lượng chức năng đang triển khai tìm kiếm.\r\n\r\nTrước đó, tại huyện Lệ Thủy, ngày 27/10, anh Lê Ngọc Hơn (22 tuổi, ở thôn Thanh Sơn, xã Thái Thủy) khi làm nhiệm vụ cứu hộ, cứu nạn tại khu vực hạ lưu đập Thanh Sơn không may bị lũ cuốn trôi.\r\n\r\nĐể đảm bảo cho người dân đi lại an toàn, Khu Quản lý đường bộ II (Cục Đường bộ Việt Nam) đã chủ động phối hợp lực lượng Cảnh sát giao thông các địa phương kịp thời phân luồng giao thông đi vào tuyến tránh; không để người dân đi vào những đoạn đường bị ngập sâu.\r\n\r\nNhững ngày qua, do mưa lớn, hiện dung tích của 35 hồ chứa thủy lợi lớn và vừa do Công ty Trách nhiệm hữu hạn Một thành viên khai thác công trình thủy lợi quản lý đạt 92,9% dung tích thiết kế; các hồ do địa phương quản lý đạt trên 98,92%.\r\n\r\nTỉnh Quảng Bình yêu cầu các đơn vị, địa phương triển khai phương án đảm bảo an toàn tính mạng và tài sản cho người dân tại vùng bị ngập lụt; cắm biển cảnh báo, phân công lực lượng túc trực, canh gác tại tuyến đường bị ngập sâu, sạt lở; tuyệt đối không để người và phương tiện qua lại tại các tuyến đường này.', 'Mưa lũ tại tỉnh Quảng Bình khiên hơn 32.700 nhà dân tại tỉnh Quảng Bình bị ngập lụt.png', 3, 0, 959, '2024-10-29 07:11:25', '2024-10-29 07:11:25', 4),
(16, 'Ấn Độ: Khói mù ô nhiễm khiến nhiều người ngạt thở', 'Thủ đô New Delhi, Ấn Độ đối mặt ô nhiễm nghiêm trọng, sương mù dày đặc bao phủ, ảnh hưởng xấu đến sức khỏe và tầm nhìn.', 'Trong tuần qua, thủ đô New Delhi liên tục nằm trong nhóm những thành phố có mức độ ô nhiễm không khí nghiêm trọng nhất thế giới. Vào thời điểm trước mùa đông, chất lượng không khí tại thành phố này thường xuống cấp đáng kể do lượng khí thải gia tăng từ hoạt động đốt rơm rạ, công nghiệp và giao thông, gây ảnh hưởng lớn đến đời sống người dân.\r\n\r\nNgười dân tại nhiều thành phố ở Ấn Độ, bao gồm New Delhi, phải thức dậy trong bầu không khí mù sương khi chỉ số chất lượng không khí quốc gia liên tục ở mức ‘rất kém’ đến ‘nghiêm trọng’.\r\n\r\nDữ liệu thời gian thực ghi nhận chỉ số chất lượng không khí tại New Delhi đã ở mức rất thấp ngay từ sáng sớm. Nhiều công trình biểu tượng, nhà cửa, đường phố của thủ đô Ấn Độ đều chìm trong lớp sương mù dày đặc, khiến tầm nhìn hạn chế và các phương tiện buộc phải bật đèn khi tham gia giao thông.\r\n\r\nÔng Balwinder Singh (New Delhi, Ấn Độ) cho biết: \"Chúng tôi đạp xe đạp hầu như mỗi ngày. Nhưng khi ô nhiễm không khí gia tăng, chúng tôi gặp rất nhiều vấn đề như bệnh hô hấp, nóng rát ở mắt và dễ bị mệt mỏi. Bịt khăn để chắn bớt khói bụi nhưng điều đó hầu như không giúp ích được gì\".\r\n\r\nAnh Himanshu Mishara (New Delhi, Ấn Độ) nói: \"Khói bụi đã bao trùm khắp thành phố và khiến chúng tôi cảm thấy ngạt thở\".\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/28/downtoearth-2024-09-11-pmgsecxm-pollution-24194992971636843184013.jpg\">\r\n\r\nÔ nhiễm không khí đang trở thành một vấn đề ngày càng nghiêm trọng tại Ấn Độ, khi mùa đông mang theo không khí lạnh làm tích tụ các khí thải từ xe cộ, khói bụi và các hạt lơ lửng trong không trung, khiến nhiều người dân cảm thấy ngột ngạt, khó thở.\r\n\r\nTiến sĩ Randeep Guleria - chuyên gia về các bệnh liên quan đến phổi và hô hấp - nhấn mạnh: \"Không chỉ tim và phổi, ô nhiễm không khí có thể ảnh hưởng đến hầu hết mọi bộ phận của cơ thể vì các hạt bụi mịn siêu nhỏ và các hóa chất khác trong không khí khi xâm nhập cơ thể sẽ đi vào nhiều cơ quan khác nhau. Do đó chúng có thể dẫn đến các bệnh có tác động lâu dài, như chứng đãng trí, các vấn đề về thần kinh, tiểu đường, hội chứng chuyển h...óa. Tất cả đều có liên quan đến ô nhiễm không khí\".\r\n\r\nViệc đốt rơm rạ ở các vùng nông thôn đã góp phần nghiêm trọng vào tình trạng ô nhiễm không khí. Tuần trước, ít nhất 16 nông dân tại Ấn Độ đã bị giới chức bắt giữ vì hành vi đốt rơm rạ trái phép, trong khi khoảng 300 người khác đã bị phạt tiền với các vi phạm tương tự.\r\n\r\nTrước tình hình này, chính quyền đã kích hoạt kế hoạch ứng phó ô nhiễm theo cấp độ, bao gồm các biện pháp như sử dụng xe quét đường và tưới nước hàng ngày trên các tuyến phố, kiểm soát bụi tại các công trường xây dựng và khu vực phá dỡ. Đồng thời, người dân được yêu cầu không đốt rác ngoài trời và hạn chế sử dụng phương tiện cá nhân.\r\n\r\nGiữa tháng 10, chính quyền New Delhi cũng đã ban hành lệnh cấm hoàn toàn việc sản xuất, lưu trữ, buôn bán và sử dụng pháo cho đến ngày 1/1/2025. Lệnh cấm này sẽ được áp dụng cả trong thời gian lễ hội ánh sáng Diwali, diễn ra vào đầu tháng 11 tới, khi việc đốt pháo trong dịp lễ này thường khiến thành phố và các khu vực ngoại ô chìm trong khói dày đặc, đẩy mức ô nhiễm không khí lên đến mức báo động trong những năm gần đây.\r\n\r\nTheo xếp hạng của IQAir, New Delhi hiện là thành phố ô nhiễm đứng thứ 2 thế giới, chỉ sau Lahore của Pakistan.\r\n\r\n', 'Thủ đô New Delhi, Ấn Độ.png', 4, 0, 1008, '2024-10-29 07:15:26', '2024-10-29 07:15:26', 13),
(17, '40.000 người châu Âu tử vong sớm mỗi năm do ô nhiễm bếp gas', 'Theo kết quả nghiên cứu mới, bếp gas góp phần gây ra cái chết sớm của khoảng 40.000 người châu Âu mỗi năm.', 'Đây là lần đầu tiên các nhà nghiên cứu có thể ước tính chính xác số ca tử vong sớm do ô nhiễm bếp gas.\r\n\r\nTrong gần 50 năm qua, thế giới đã nhận thức được mối nguy hiểm của khí Nitrogen dioxide (NO2) phát ra từ bếp gas. Hiện nay, các nhà nghiên cứu tại Trường Khoa học Sức khỏe của Đại học Jaume I ở Tây Ban Nha đã ước tính số ca tử vong hàng năm bằng cách liên kết các nghiên cứu sức khỏe hiện có với các chỉ số NO2 tại những gia đình ở châu Âu.\r\n\r\nHọ phát hiện ra rằng ở 14 quốc gia châu Âu, các điều kiện nguy hiểm đã được tạo ra bên trong ngôi nhà khi khói từ bếp gas kết hợp với các yếu tố gây ô nhiễm môi trường xung quanh, phá vỡ các hướng dẫn của Tổ chức Y tế Thế giới (WHO).\r\n\r\n\"Trở lại năm 1978, chúng ta lần đầu tiên biết rằng ô nhiễm Nitrogen dioxide cao hơn nhiều lần đối với bếp sử dụng gas so với bếp điện. Nhưng chỉ đến bây giờ chúng ta mới có thể đưa ra số lượng các trường hợp tử vong sớm (do bếp gas)\" - tác giả chính của nghiên cứu mới, Tiến sĩ Juana Maria Delgado-Saborit, cho biết.\r\n\r\nKhoảng 1/3 các ngôi nhà ở châu Âu nấu bằng gas và những ngôi nhà này có xu hướng có mức NO2 cao nhất. Italy, Ba Lan, Romania, Pháp và Anh ghi nhận tỷ lệ tử vong sớm do bếp gas cao nhất. Ô nhiễm tệ nhất ở những ngôi nhà có hệ thống thông gió kém và nấu ăn trong thời gian lâu hơn.\r\n\r\nChất lượng không khí trong nhà là một vấn đề sức khỏe nghiêm trọng vì người dân châu Âu dành hầu hết thời gian ở trong nhà, trong khi các tòa nhà ít có khí tươi hơn vì được thiết kế kín gió hơn.\r\n\r\nBếp gas cũng được phát hiện rò rỉ khí methane - một loại khí nhà kính mạnh, ngay cả khi bếp đã được tắt.\r\n\r\nĐối với ô nhiễm bên ngoài, NASA đã ghi nhận giảm đáng kể tình trạng ô nhiễm NO2 ở các thành phố châu Âu trong những thập kỷ gần đây nhờ những quy định hạn chế khí thải xe và công nghệ xe ô tô tiên tiến của EU.\r\n\r\nNghiên cứu này có thể là bước đột phá quan trọng về ảnh hưởng sức khỏe của con người khi nấu ăn bằng gas.', 'bếp ga.png', 4, 0, 615, '2024-10-29 07:18:16', '2024-10-29 07:18:16', 14),
(18, 'Philippines nỗ lực khắc phục hậu quả sau bão Trami', 'Đến ngày 28/10, số người tử vong do bão Trami ở Philippines đã tăng lên 136, trong khi 39 người vẫn mất tích do bị nước lũ cuốn trôi hoặc sạt lở vùi lấp.', 'Theo số liệu của Hội đồng Quản lý và Giảm thiểu rủi ro thảm họa quốc gia Philippines. Nhiều gia đình đã mất trắng hoàn toàn sau khi bão Trami đi qua. Hiện Chính phủ Philippines đang nỗ lực khắc phục hậu quả sau cơn bão.\r\n\r\nNhững gì còn sót lại tại tỉnh Batangas - một trong nhiều khu vực tại Philippines bị thiệt hại nặng nề do bão Trami - là cây cối, cột điện đổ ngổn ngang, đường sá bị phá hủy. Có người may mắn vẫn giữ được nhà để ở, nhưng có cả những gia đình bị mất trắng. Những gì còn lại với họ chỉ là một vài bộ quần áo và vật dụng cần thiết.\r\n\r\nBà Vilima Briyuso - người dân Philippines - nói: \"Chúng tôi chẳng còn gì nữa. Chúng tôi cũng không còn nhà để về. Chúng tôi biết đi đâu bây giờ. Lúc đó chúng tôi chỉ biết chạy thật nhanh, bỏ lại tất cả mọi thứ, ngay cả điện thoại\".\r\n\r\nAnh Raynaldo Dejucos cho biết: \"Trận lở đất ở chỗ ngọn đồi đổ thẳng xuống nhà tôi, 5 đứa con và vợ tôi lúc đó còn đang ngủ, tôi thì đi làm. Tôi liên tục gọi tên vợ tôi - Salve, và tên các con. Rồi tôi nhìn thấy một cái chân và hét lên cầu cứu mọi người. Cả gia đình tôi bị chôn vùi ở đây cho đến khi tôi đào được vợ tôi, đứa con út vẫn còn đang ẵm ngửa, hai đứa con trai, rồi đến đứa con gái và một bé trai nữa\".\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/29/ap24301234425260-92961807768249009864069.jpg\">\r\n\r\nThống kê sơ bộ cho thấy phần lớn các nạn nhân thiệt mạng do bão tập trung ở khu vực Bicol và tỉnh Batangas. Chính quyền Philippines cho biết gần 240.000 người đang trú ẩn tại các trung tâm sơ tán, 7.510 hành khách bị kẹt tại các cảng và 36 chuyến bay bị hủy trong những ngày vừa qua.\r\n\r\nHiện các nhân viên cứu hộ vẫn đang chạy đua với thời gian để tiếp cận những người bị mắc kẹt tại các khu vực bị cô lập do lũ lụt và tìm kiếm người mất tích sau bão Trami.\r\n\r\nMỗi năm có khoảng 20 trận bão lớn tràn vào Philippines hoặc vùng biển xung quanh, gây thiệt hại về nhà cửa, cơ sở hạ tầng và cướp đi nhiều sinh mạng. Một nghiên cứu gần đây cho thấy các trận bão ở khu vực châu Á - Thái Bình Dương ngày càng hình thành gần bờ biển hơn, mạnh lên nhanh chóng và kéo dài lâu hơn trên đất liền do biến đổi khí hậu.\r\n\r\nCơ quan Quản lý Thiên văn, Địa vật lý và Khí quyển Philippines hôm 28/10 cho biết từ nay đến cuối năm, có từ 2 đến 8 cơn bão nhiệt đới nữa có thể đi vào Khu vực trách nhiệm theo dõi của Philippines. Nước này hiện đang theo dõi sát cơn bão mới nhất mang tên Kong-rey.', 'Vụ lở đất ở Talisay khiến hàng nghìn người bị mất nhà cửa và một số dân làng thiệt mạng, ngày 26-10.png', 4, 1, 1040, '2024-10-29 07:23:09', '2024-10-29 07:23:09', 10),
(19, 'Bom Nga phá hủy tòa nhà lịch sử ở Kharkov, Ukraine', 'Một vụ đánh bom của Nga hôm 28/10 đã phá hủy phần lớn tòa nhà Derzhprom, một trong những địa danh nổi tiếng nhất có từ những năm 1920 tại thành phố Kharkov của Ukraine.', 'Sáu người đã bị thương trong cuộc không kích của Nga diễn ra vào lúc 21h ngày 28/10 (theo giờ địa phương).\r\n\r\nTại thành phố Kryvyi Rih ở miền trung Ukraine, một tên lửa của Nga đã đánh trúng một tòa nhà chung cư 3 tầng, khiến một người thiệt mạng và ít nhất 11 người bị thương.\r\n\r\nTổng thống Ukraine Zelensky đã lên án vụ tấn công vào tòa nhà Derzhprom (tòa nhà Công nghiệp Nhà nước) ở thành phố Kharkov - một trong những hình mẫu nổi bật nhất về kiến ​​trúc kiến ​​tạo thời Liên Xô cũ và được mệnh danh là tòa nhà chọc trời đầu tiên của Liên Xô.\r\n\r\nViết trên mạng xã hội X, Tổng thống Zelensky cũng lên án vụ tấn công vào Kryvyi Rih - quê hương của ông, đồng thời kêu gọi tiếp tục nỗ lực buộc Tổng thống Nga Vladimir Putin phải chấm dứt cuộc chiến kéo dài hơn 2,5 năm ở Ukraine.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/29/29102024-bom-nga-pha-huy-toa-nha-2-35583935066160258138209.png\">\r\n\r\nVideo của Reuters Television cho thấy một số phần của tòa nhà Derzhprom bị phá hủy thành đống đổ nát và hầu như tất cả kính cửa sổ đều vỡ tan.\r\n\r\nTòa nhà Derzhprom - được đưa vào danh sách dự kiến của Di sản Thế giới UNESCO - được hoàn thành vào năm 1928 khi Kharkov là thủ đô đầu tiên của Cộng hòa Xã hội chủ nghĩa Xô viết Ukraine.\r\n\r\nCuộc tấn công của Nga cũng nhằm vào một cơ sở y tế. Các cuộc không kích trước đó đã phá hủy một tòa nhà chung cư và cơ sở lưu trữ.\r\n\r\nThành phố Kharkov đã phải hứng chịu hàng loạt vụ tấn công của Nga từ những ngày đầu của cuộc chiến Nga - Ukraine và kể từ đó thường xuyên trở thành mục tiêu của các cuộc không kích.\r\n\r\nNga đã phủ nhận việc nhắm mục tiêu vào dân thường trong cuộc chiến ở Ukraine - được phát động vào tháng 2/2022.', 'Vụ lở đất ở Talisay khiến hàng nghìn người bị mất nhà cửa và một số dân làng thiệt mạng, ngày 26-10.png', 4, 1, 1245, '2024-10-29 07:34:27', '2024-10-29 07:34:27', 15),
(20, 'Philippines sắp đón bão cường độ mạnh Kong-rey ngay sau siêu bão Trami chết người', 'Ngày 28/10, Philippines tiếp tục ban bố cảnh báo về cơn bão mới, vài ngày sau khi hơn 100 người thiệt mạng vì siêu bão tồi tệ nhất trong năm Trami (Trà Mi).', 'Gần 1 triệu người dân Philippines vẫn đang trú ẩn tại các trung tâm sơ tán sau khi mất nhà cửa hoặc bị lũ lụt do bão nhiệt đới Trami tấn công hôm 22/10.\r\n\r\nNgày 28/10, Cơ quan thời tiết quốc gia Philippines cho biết bão nhiệt đới Kong-rey sẽ mang theo mưa lớn và gió mạnh đổ bộ vào đất liền trong những giờ tới, gây ra biển động ngoài khơi bờ biển phía Đông.\r\n\r\nKong-rey được dự báo mạnh lên thành bão vào thứ Ba (29/10) và đi qua gần các đảo nhỏ của Philippines ở phía Bắc sớm nhất là vào ngày 30/4 - Cơ quan thời tiết quốc gia thông báo trong một bản tin.\r\n\r\nMức cảnh báo bão thấp nhất trong 5 cấp độ đang được áp dụng trên bờ biển Đông Bắc Philippines.\r\n\r\n\"Xét đến diễn biến hiện tại, không loại trừ khả năng bão Kong-rey sẽ dịch chuyển về phía Tây và tiến gần đến Philippines nhanh hơn so với dự báo trước đó\" – Cơ quan thời tiết quốc gia cảnh báo.\r\n\r\nBão Kong-rey dự kiến sẽ đổ bộ vào Đài Loan (Trung Quốc) với cường độ khá mạnh vào sáng sớm ngày 1/11.\r\n\r\nKhoảng 20 cơn bão lớn và bão nhiệt đới đổ bộ vào Philippines hoặc vùng biển xung quanh mỗi năm, phá hủy nhà cửa và cơ sở hạ tầng, đồng thời giết chết hàng chục người.\r\n\r\nTheo thống kê mới nhất của cơ quan thảm họa quốc gia Philippines, bão Trami đã khiến ít nhất 116 người thiệt mạng, trong khi 39 người mất tích, trở thành cơn bão nghiêm trọng nhất trong năm nay tại đảo quốc này.', 'Bão Trami vừa gây thiệt hại nặng nề tại Philippines.png', 4, 1, 2000, '2024-10-29 07:37:59', '2024-10-29 07:37:59', 16),
(21, 'Giá vàng giữ mức 89 triệu đồng/lượng', 'Sáng nay (29/10), giá vàng trong nước \"án binh bất động\", trong khi giá vàng thế giới tiếp tục đi lên.', 'Cụ thể, tại thời điểm 9 giờ 10 phút, Công ty Vàng bạc đá quý Sài Gòn SJC công bố giá vàng nhẫn 87 - 89 triệu đồng/lượng (mua vào - bán ra), giữ nguyên mức giá niêm yết ở cả hai chiều mua vào và bán ra so với chốt phiên hôm qua.\r\n\r\nTập đoàn Vàng bạc đá quý DOJI niêm yết giá vàng nhẫn ở mức 87,7 - 88,7 triệu đồng/lượng (mua vào - bán ra), giữ nguyên mức giá niêm yết ở cả hai chiều mua vào và bán ra so với chốt phiên hôm qua.\r\n\r\nCông ty cổ phần Vàng bạc đá quý Phú Nhuận công bố giá vàng nhẫn 87,6 - 88,89 triệu đồng/lượng (mua vào - bán ra), giữ nguyên mức giá niêm yết ở cả hai chiều mua vào và bán ra so với chốt phiên hôm qua.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/06/11/vang-83650899469710851631417.jpg\">\r\n\r\nCùng với đó, giá vàng miếng SJC cũng không đổi. Theo đó, Công ty Vàng bạc đá quý Sài Gòn (SJC) công bố giá vàng miếng SJC ở mức 87 - 89 triệu đồng/lượng (mua vào - bán ra), giữ nguyên mức giá niêm yết ở cả hai chiều mua vào và bán ra so với chốt phiên cuối tuần qua.\r\n\r\nTập đoàn Vàng bạc đá quý DOJI công bố giá bán vàng miếng SJC ở mức 87 - 89 triệu đồng/lượng (mua vào - bán ra), giữ nguyên mức giá niêm yết ở cả hai chiều mua vào và bán ra so với chốt phiên cuối tuần qua.\r\n\r\nNhư vậy, giá vàng miếng không thay đổi sau 6 phiên liên tiếp. So với đầu năm, mỗi lượng vàng miếng tăng 15 triệu đồng, tương đương mức tăng 19% còn vàng nhẫn tăng 25 triệu đồng/lượng, tương đương 40%.\r\n\r\nGiá vàng thế giới\r\n\r\nTrên thị trường thế giới, sáng nay giá vàng tiếp tục đi lên do thu hút nhiều nhà đầu tư chính thống tham gia. Giá vàng thế giới được niêm yết trên Kitco ở ngưỡng 2.741 USD/ounce, tăng 9 USD/ounce so với đầu giờ sáng qua.\r\n\r\nGiá vàng tăng trở lại khi vàng trở thành kênh đầu tư hiệu quả, thu hút một loạt các nhà phân tích chính thống tham gia cuộc chơi.\r\n\r\nTheo đó, các nhà đầu tư quốc tế vẫn xem vàng là kênh đầu tư trú ẩn an toàn, trong bối cảnh xung đột địa chính trị căng thẳng và kết quả bầu cử tổng thống Mỹ sắp tới (ngày 5/11) còn nhiều bất định.\r\n\r\nĐà tăng của vàng hiện đang bị kìm hãm do đồng USD tiếp đà phục hồi và lợi suất trái phiếu kho bạc Mỹ tăng.\r\n\r\nHiện tại, các nhà giao dịch vẫn đang chờ đợi một loạt dữ liệu kinh tế của Mỹ trong tuần này để biết thêm về đường hướng chính sách trong tương lai của Cục Dự trữ Liên bang Mỹ (Fed).\r\n\r\nTheo công cụ FedWatch của CME, lạm phát của Mỹ được kìm hãm, thị trường đang kỳ vọng 96,9% khả năng Fed sẽ cắt giảm 25 điểm cơ bản tại cuộc họp vào 7/11 và còn 3,1% giữ nguyên lãi suất như hiện tại.\r\n\r\nÔng Kevin Grady, Chủ tịch công ty Phoenix Futures and Options tại Mỹ, dự đoán giá vàng thế giới có thể đạt 3.000 USD/ounce trong quý đầu năm 2025.\r\n\r\nMarc Chandler, Giám đốc tại Bannockburn Global Forex, nhận định nếu giá vàng thủng mức 2.700 USD/ounce, nhà đầu tư dài hạn có thể chịu áp lực lớn. Ông dự đoán giá vàng sẽ dao động từ 2.600 - 2.800 USD/ounce.', 'vang.png', 5, 1, 1148, '2024-10-29 07:54:37', '2024-10-29 07:54:37', 17),
(22, 'Trỗi dậy thần kỳ, Nvidia đuổi kịp và có thể vượt qua Apple như thế nào?', 'Nvidia đang tiến nhanh như vũ bão và đang đe dọa soán ngôi của Apple về quy mô vốn hóa thị trường.', 'Chỉ một thập kỷ sau khi được định giá bằng khoảng 2% Apple về vốn hóa thị trường, công ty chuyên sản xuất chip trí tuệ nhân tạo Nvidia đang tiến gần đến việc vượt qua nhà sản xuất iPhone để trở thành công ty có giá trị thứ hai thế giới và thậm chí vượt qua Microsoft để trở thành công ty lớn nhất thế giới. Theo các chuyên gia, đây thực sự là sự \"trỗi dậy thần kỳ\". \r\n\r\nTrỗi dậy thần kỳ\r\n\r\nHiện, giá trị vốn hóa thị trường của Nvidia đang ở mức khoảng 2,8 nghìn tỷ USD, với mức định giá 2,9 nghìn tỷ USD của Apple và mức giá 3,2 nghìn tỷ USD của Microsoft. \r\n\r\nNhững con số này, theo đánh giá của tờ Forbes là \"bước ngoặt đáng kinh ngạc so với cuối năm 2014\". Ở thời điểm đó, giá trị thị trường của Nvidia ở mức 11 tỷ USD, Apple ở mức 643 tỷ USD và Microsoft ở mức 382 tỷ USD.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/06/03/screen-shot-2024-06-03-at-7-00-58-am-31657142-901e-44fd-ae3a-febed1522c88-1d58207f-a8bf-46bd-be79-89061930f29b.png\">\r\n\r\nVới những đối thủ khác, Nvidia đã cho những gã khổng lồ công nghệ lâu đời như Google và Amazon \"hít khói\" vào đầu năm nay với mức tăng cổ phiếu khoảng 130%. \r\n\r\nTheo tính toán, đến thời điểm hiện tại, Nvidia sẽ chỉ cần tăng giá cổ phiếu thêm 35 USD nữa để vượt qua Apple về giá trị thị trường. \r\n\r\nPhố Wall nhận định, con đường để Nvidia bắt kịp và vượt qua Táo khuyết là không con xa.\r\n\r\nDựa trên dự báo từ 40 nhà phân tích Phố Wall về mục tiêu giá trong 12 tháng tới cho Nvidia, cổ phiếu của hãng có thể đạt mức trung bình là 1.197,64 USD (tương đương mức tăng 9,24% so với mức giá đóng cửa gần nhất) với một số dự báo có thể đạt mức cao tới 1.400 USD (tăng 28%).\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/06/03/screen-shot-2023-08-24-at-9-39-26-am-27992e0a-3128-4bf4-baa3-40519fbb51dd.jpg\">\r\n\r\nCổ phiếu Apple đã biến động với tốc độ quá chậm so với Nvidia: cổ phiếu của hãng chỉ tăng 3,6% trong năm tháng đầu năm 2024. Trong khi đó, đà tăng trưởng của cổ phiếu Nvidia diễn ra khá nhanh chóng và bền vững - các nhà đầu tư của công ty đã tận hưởng mức tăng giá tới 127% trong năm tháng qua.\r\n\r\nĐứng đầu trong thị trường nóng nhất hành tinh\r\n\r\nTheo nghiên cứu của Goldman Sachs, Nvidia hiện là công ty lớn nhất trong một thị trường \"nóng nhất hiện nay\" - sản xuất chip cho sự phát triển của trí tuệ nhân tạo. \r\n\r\nNvidia đang thiết kế khoảng 80% chip bán dẫn cho công nghệ AI. Điều này, theo Goldman Sachs, mang lại cho Nvidia tiềm năng thu nhập to lớn trong tương lai, đặc biệt khi cơn khát chip trên toàn cầu đang ngày một tăng. Những đối thủ lớn nhất của Nvidia như Apple và Microsoft cũng đang là khách hàng của Nvidia. \r\n\r\nCác nhà phân tích tại ngân hàng Morgan Stanley đánh giá, đà tăng trưởng của Nvidia “không có dấu hiệu chậm lại” và “vẫn rất mạnh mẽ thông qua quá trình chuyển đổi sản phẩm”.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/06/03/nvidia-big-tech-be671397-18aa-410b-88e7-f43836477c3b.png\">\r\n\r\nTờ Forbes bình luận, \"nếu bạn tin rằng chúng ta đang ở giai đoạn đầu của một \"cuộc cách mạng công nghiệp mới\" nhờ AI, như lời của CEO Jensen Huang của Nvidia từng nói thì, đầu tư vào Nvidia cũng giống như đầu tư vào một công ty chế tạo các bộ phận quan trọng của động cơ hơi nước vào những năm 1700\". \r\n\r\nTheo nhà phân tích Hans Mosesmann của Rosenblatt: \"Chúng ta đang ở giai đoạn đầu của cái gọi là khởi đầu của mọi chu kỳ\". Ông đã đặt mục tiêu 1.400 USD cho cổ phiếu Nvidia, từ tháng 2, khi cổ phiếu của hãng giao dịch ở mức dưới 800 USD. \r\n\r\nTuy nhiên, ông Sid Sheth, đồng sáng lập của đối thủ đầy tham vọng D-Matrix, cho biết: \"Nvidia muốn có 100%, nhưng khách hàng sẽ không thích Nvidia có 100%. Đó là một cơ hội quá lớn. Sẽ là quá không lành mạnh nếu bất kỳ một công ty nào lấy đi tất cả\". \r\n\r\nĐược thành lập vào năm 2019, D-Matrix có kế hoạch phát hành thẻ bán dẫn cho máy chủ vào cuối năm nay nhằm mục đích giảm chi phí và độ trễ khi chạy các mô hình AI. Công ty đã huy động được nguồn đầu tư lên tới 110 triệu USD vào tháng 9.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/06/03/screen-shot-2024-06-03-at-7-00-04-am-2390d78a-0b47-4925-ad7c-c3b8d8eac0b1.png\">\r\n\r\nNgoài D-Matrix, từ các tập đoàn đa quốc gia đến các công ty khởi nghiệp non trẻ đang tranh giành một phần của thị trường chip AI béo bở. Theo các nhà phân tích, quy mô của thị trường này có thể đạt doanh thu hàng năm 400 tỷ USD trong 5 năm tới.\r\n\r\nNvidia đã chạm mốc doanh thu khoảng 80 tỷ USD trong 4 quý vừa qua và Bank of America ước tính tập đoàn này đã bán được 34,5 tỷ USD chip AI vào năm ngoái.\r\n\r\nNhiều công ty sử dụng GPU của Nvidia đang đặt cược rằng một kiến ​​trúc khác hoặc sự đánh đổi nhất định có thể tạo ra con chip tốt hơn cho các tác vụ cụ thể. Các nhà sản xuất thiết bị cũng đang phát triển công nghệ có thể thực hiện nhiều tính toán cho AI hơn.\r\n\r\nÔng Fernando Vidal, đồng sáng lập của 3Fourteen Research, nói với CNBC: \"Không ai có thể phủ nhận rằng ngày nay Nvidia là phần cứng mà bạn muốn đào tạo và chạy các mô hình AI. Nhưng đã có những tiến bộ ngày càng tăng trong việc san bằng khoảng cách của sân chơi, từ những công ty siêu quy mô làm việc trên chip của riêng họ, cho đến cả những công ty khởi nghiệp nhỏ\".', 'Nvidia đang chuẩn bị đuổi kịp và có thể vượt qua Apple về giá trị thị trường.png', 5, 0, 631, '2024-10-29 08:03:32', '2024-10-29 08:03:32', 18),
(23, 'Mark Zuckerberg và Facebook mất bao nhiêu tiền sau sự cố sập toàn cầu?', 'Sự cố sập toàn cầu với cả ba nền tảng Facebook, Instagram và Messenger khiến Meta phải trả một cái giá đắt đỏ.', 'Nói với tờ DailyMail, ông Dan Ives, giám đốc điều hành Wedbush Securities tính toán, CEO Mark Zuckerberg đã mất khoảng 100 triệu USD chỉ sau gần 2 giờ các nền tảng chính  Facebook, Instagram và Messenger  sập trên toàn cầu .\r\n\r\nGiá cổ phiếu của Meta cũng giảm 1,5% khi các báo cáo sự cố tràn ngập trên toàn thế giới vào khoảng 10 giờ sáng theo giờ Mỹ. Sau đó, mã cổ phiếu này giảm tiếp 1,6%. Đáng chú ý, trước đó cổ phiếu này đang trong xu hướng tăng nhiều phiên gần nhất.\r\n\r\nTheo dữ liệu cập nhật thời gian thực của Forbes cho tới sáng nay 6/3 (theo giờ Việt Nam), khối tài sản ròng của Mark Zuckerberg đã giảm 1,56%, tương đương mức giảm 2,7 tỷ USD. Hiện ông chủ Meta đang nắm trong tay 171,9 tỷ USD và là người giàu thứ 4 thế giới.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/562122370168008704/2024/3/6/markzuckerberg-1709692714775831243894.png\">\r\n\r\nTheo ông Ives, Meta có thể sẽ không tiết lộ mức thiệt hại của sự cố này. Hiện ông lớn công nghệ đang giải thích nguyên nhân của sự cố là \"các vấn đề kỹ thuật\" tuy nhiên, những gì thực sự đang diễn ra vẫn là một bí ẩn.\r\n\r\n\"Chúng tôi biết mọi người đang gặp phải khó khăn khi truy cập các dịch vụ của chúng tôi. Chúng tôi đang giải quyết vấn đề này\", người phát ngôn Meta, ông Andy Stone viết trên mạng xã hội X (trước đây là Twitter).\r\n\r\nDailyMail dẫn lời nguồn tin trong nội bộ Facebook cho biết,  hệ thống nội bộ của công ty không hoạt động, điều này có thể dẫn đến sự cố.\r\n\r\nSự cố của Meta xảy ra trên phạm vi toàn cầu trong thời gian khoảng hơn một giờ từ hơn 22h (giờ Việt Nam). Người dùng Facebook, Messenger bị thoát tài khoản và không thể đăng nhập lại.\r\n\r\nĐến 23h20, một số đã có thể truy cập trở lại. Tuy nhiên, nhiều người không thể đăng nhập lại bởi các nguyên nhân như quên mật khẩu, thiếu thiết bị xác thực, không nhận được mã xác thực.\r\n\r\nTrên Instagram, người dùng di động nhận thấy bảng tin cá nhân không được làm mới.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/562122370168008704/2024/3/5/img-2785-png-9821-1709663108-1709680221755600802989.jpg\">\r\n\r\nTrang web DownDetector dẫn số liệu thống kê cho thấy mạng xã hội Facebook đã ngừng hoạt động ở mức đỉnh điểm là khoảng 500.000 báo cáo vào lúc 15h30 GMT ngày 5/3 (22h30 cùng ngày theo giờ Hà Nội), trong khi Instagram đạt mức đỉnh điểm với khoảng 70.000 lượt báo cáo cùng lúc.\r\n\r\nThreads - đối thủ của Twitter ra mắt hồi năm 2023 - cũng phải đối mặt với tình trạng ngừng hoạt động và giao diện lập trình ứng dụng WhatsApp Business, dịch vụ tin nhắn của Meta, cũng phát sinh một số vấn đề.\r\n\r\nTrang trạng thái của Facebook, dành cho các nhà quảng cáo, thừa nhận đã bị \"gián đoạn nghiêm trọng\" và \"các nhóm kỹ thuật đang tích cực tìm cách giải quyết vấn đề một cách nhanh nhất có thể\".\r\n\r\nFacebook hiện là nền tảng truyền thông xã hội lớn nhất thế giới với 3 tỷ người dùng hoạt động hàng tháng, trong khi Instagram thu hút khoảng 1,35 tỷ người dùng.', 'Elon Musk chế giễu sự cố của Meta.png', 5, 1, 1008, '2024-10-29 08:08:00', '2024-10-29 08:08:00', 18);
INSERT INTO `items` (`id`, `title`, `excerpt`, `content`, `image`, `category`, `featured`, `views`, `created_at`, `updated_at`, `author`) VALUES
(24, 'Sa thải nhân viên, cổ phiếu giảm mạnh, Tesla và Elon Musk đối mặt với những thử thách chưa từng có', 'Cổ phiếu Tesla xuống mức thấp nhất trong gần một năm sau khi công ty quyết định đợt cắt giảm nhân sự mới.', 'Theo tờ CNBC, thông thường cổ phiếu của các công ty thường tăng vọt sau khi cắt giảm nhân sự bởi Phố Wall nhìn thấy những triển vọng phục hôi hoặc triển vọng lợi nhuận mới.\r\n\r\nTuy nhiên, đó không phải là cách các nhà đầu tư \"đối xử\" với tin tức mới nhất của Tesla.\r\n\r\nCổ phiếu của nhà sản xuất xe điện đã giảm gần 6% vào ngày thứ Hai và 2,7% khác vào thứ Ba (theo giờ Mỹ) sau khi thông báo cắt giảm nhân sự.\r\n\r\nĐây là mức giảm cổ phiếu mạnh nhất của Tesla từ tháng 4 năm ngoái.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/1000/562122370168008704/2023/10/17/18102023-tesla-thu-hoi-gan-55000-xe-model-x-1697581104759893885975.png\">\r\n\r\nCuối tuần, CEO Elon Musk thông báo công ty có kế hoạch sa thải hơn 10% lực lượng lao động toàn cầu, tương đương với khoảng 14 nghìn người.\r\n\r\n\"Không có gì tôi ghét hơn, nhưng quyết định này phải được thực hiện.Cắt giảm nhân sự sẽ cho phép chúng ta trở nên tinh gọn, đổi mới và hướng tới chu kỳ giai đoạn tăng trưởng tiếp theo\", Musk viết trong một bản ghi nhớ gửi nhân viên.\r\n\r\nTính đến tháng 12/2023, Tesla tuyển dụng 140.473 nhân viên. Lần cuối Musk thông báo cắt giảm nhân sự là năm 2022, sau khi nói với các quan chức khác về việc ông cảm thấy \"vô cùng tồi tệ\" về nền kinh tế.\r\n\r\nCổ phiếu Tesla đã giảm 29% trong quý đầu tiên của năm nay. Đây là giai đoạn tồi tệ nhất với ông lớn xe điện kể từ cuối năm 2022.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/1000/562122370168008704/2023/8/8/xezww3svnvla3i23moi2lnrt6i-1691464278160631745051.jpg\">\r\n\r\nMức giảm trong quý I vừa qua cũng là đợt giảm mạnh thứ ba kể từ khi công ty thực hiện phát hành cổ phiếu lần đầu ra công chúng (IPO) vào năm 2010. Cổ phiếu hiện nay của Tesla thấp hơn 60% so với mức đỉnh đạt được vào tháng 11/2021.\r\n\r\nTờ CNBC nói thêm, những đợt sa thải trước đây của Tesla không gây ra tâm lý bi quan như vậy trên thị trường.\r\n\r\nNăm 2018, khi Tesla cắt giảm 9% nhân sự, cổ phiếu đã tăng hơn 3%. Vào năm 2022, cổ phiếu đã giảm 9% sau những báo cáo ban đầu về việc sa thải nhân viên nhưng đã phục hồi khi CEO Elon Musk đưa ra bình luận làm rõ vài ngày sau đó.\r\n\r\nVậy nguyên nhân là gì? Câu trả lời là Tesla của hôm nay có thể đang rơi vào một tình thế khó khăn khác.\r\n\r\nTheo báo cáo mới được công bố đầu tháng, lượng xe giao tới tay khách hàng của nhà sản xuất ô tô điện đã giảm lần đầu tiên kể từ năm 2020, khi đại dịch COVID làm gián đoạn sản xuất.\r\n\r\nTại Trung Quốc, Tesla đã phải đối mặt với sự cạnh tranh dữ dội từ các nhà sản xuất xe điện trong nước, bao gồm BYD và nhà sản xuất điện thoại lấn sân sang mảng xe điện, Xiaomi.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/1000/562122370168008704/2024/1/31/byd-1706664265049181739581.jpg\">\r\n\r\nTrước khi quyết định sa thải nhân viên, Tesla đã giảm giá và đưa ra nhiều ưu đãi cho người mua, bất chấp việc này có thể \"bào mòn lợi nhuận\". Tuần trước, công ty cho biết họ đang giảm giá việc đăng ký hệ thống hỗ trợ lái xe cao cấp xuống một nửa cho khách hàng ở Mỹ.\r\n\r\nTheo dữ liệu từ Kelley Blue Book, giá xe điện nói chung đã thấp hơn 9,7% so với cùng kỳ năm ngoái vào tháng 3, nhờ \"các gói ưu đãi mạnh mẽ\". Theo tính toán, giá của xe Tesla đã chạm đáy vào tháng 1 nhưng có vẻ như người tiêu dùng vẫn chưa cảm thấy thực sự hấp dẫn.\r\n\r\nCũng theo nhận định của các chuyên gia, đợt bán tháo hôm thứ Hai không chỉ liên quan đến việc sa thải nhân viên, mà còn do tác động của vấn đề nhân sự.\r\n\r\nCác giám đốc cấp cao của Tesla là Drew Baglino và Rohan Patel tuyên bố sẽ rời công ty. Ông Baglino đã làm việc với Tesla từ những năm đầu thành lập trong khi ông Patel gia nhập Tesla vào năm 2016 sau khi làm cố vấn cấp cao cho cựu Tổng thống Barack Obama về các vấn đề khí hậu và các chương trình khác.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/562122370168008704/2024/4/17/83671185-0-image-a-131713203704301-17133193338901236855287.jpg\">\r\n\r\nTrong bản ghi nhớ gửi nhân viên, ông Musk khẳng định \"điều cực kỳ quan trọng là phải xem xét mọi khía cạnh của công ty để giảm chi phí và tăng năng suất\". Tuy nhiên, các nhà phân tích và nhà đầu tư nhận thấy vấn đề quan trọng nhất về nhu cầu người dùng.\r\n\r\nTheo FactSet, 18 nhà phân tích đã hạ mục tiêu giá của họ đối với cổ phiếu Tesla trong tháng này, trong khi không có nhà phân tích nào lạc quan hơn.\r\n\r\n\"Ngay khi bạn nghĩ rằng tin tức không thể tồi tệ hơn nữa đối với Tesla, thì chúng tôi lại đặt ra các câu hỏi về nhu cầu xe điện trong vài quý vừa qua\", ông Doug Clinton của Deepwater Asset Management, cho biết trên chương trình \"Squawk Box\" của CNBC.\r\n\r\nĐầu năm nay, Tesla thừa nhận, mức tăng trưởng năm 2024 có thể \"thấp hơn đáng kể\" so với năm trước. Công ty cho biết họ đang ở giữa hai làn sóng tăng trưởng xe điện nhưng đã từ chối đưa ra chỉ dẫn cho năm 2024.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/1000/562122370168008704/2023/7/18/elon-musk-16896578280561699235678.png\">\r\n\r\nNgoài sự sự cạnh tranh ngày càng khốc liệt và sự thay đổi nhanh đến chóng mặt của ngành công nghiệp xe điện, theo các chuyên gia còn những điều khó lường xảy ra với Elon Musk.\r\n\r\nTỷ phú công nghệ đang phải này đã phải đối mặt với sự giám sát chặt chẽ từ cơ quan quản lý về các giao dịch tại mạng xã hội X (trước đây là Twitter). Bên cạnh đó, các cổ đông đã bày tỏ lo ngại về việc liệu Elon Musk có dành đủ sự quan tâm cho Tesla hay không.\r\n\r\nÔng Musk giữ vị trí CEO của SpaceX, sở hữu nền tảng X, bắt đầu thực hiện công ty trí tuệ nhân tạo xAI và điều hành công ty nghiên cứu Neuralink cũng như một công ty khác là The Boring Co.\r\n\r\nÔng Musk từng nhấn mạnh, ông chưa bỏ lỡ bất kỳ cuộc họp \"quan trọng\" nào tại Tesla và ông không \"hoàn toàn vắng mặt trong hoạt động\" của công ty sản xuất xe điện.', 'Những đợt sa thải trước đây của Tesla không gây ra tâm lý bi quan như vậy trên thị trường.png', 5, 1, 942, '2024-10-29 08:14:06', '2024-10-29 08:14:06', 18),
(25, 'Tỷ phú Elon Musk bị kiện vì khoản thưởng triệu đô cho cử tri', 'Tỷ phú Elon Musk đang đối mặt với một vụ kiện lớn liên quan tới chương trình tặng thưởng 1 triệu USD mỗi ngày của ông.', 'Theo nội dung đơn kiện, Biện lý Larry Krasner cho rằng chương trình tặng thưởng của Elon Musk và tổ chức America PAC thực chất là một hình thức \"xổ số bất hợp pháp\" vì vi phạm luật của bang Pennsylvania.\r\n\r\nBộ Tư pháp Mỹ cũng đã gửi cảnh báo, nhấn mạnh rằng chương trình này có thể phạm luật liên bang khi cấm trả tiền để thúc đẩy cử tri đi bỏ phiếu.\r\n\r\nChương trình của Musk hứa hẹn trao 1 triệu USD mỗi ngày cho cử tri tại các bang chiến địa nếu họ ký vào bản kiến nghị ủng hộ Tu chính án Thứ nhất và Thứ hai của Hiến pháp để bảo vệ quyền tự do ngôn luận và sở hữu vũ khí. Nhiều người đã trúng thưởng.\r\n\r\n<img src=\"https://cdn-images.vtv.vn/thumb_w/640/66349b6076cb4dee98746cf1/2024/10/29/typhu-87792491832096023948486.jpg\">\r\n\r\nElon Musk gây chú ý khi đã chi hơn 132 USD để hỗ trợ cựu Tổng thống Donald Trump và các ứng viên đảng Cộng hòa, khiến ông trở thành một trong những nhà tài trợ chính của kỳ bầu cử năm nay. Ông Musk cho biết mục tiêu chương trình này là khuyến khích cử tri và bảo vệ các giá trị hiến pháp.\r\n\r\nTuy nhiên, nhiều ý kiến tỏ ra lo ngại rằng đây là một cách thức tinh vi nhằm tác động đến kết quả bầu cử. Hiện chưa rõ liệu Elon Musk có phải ngừng chương trình này hay không, nhưng vụ kiện đang tạo thêm sức nóng cho cuộc đua cam go vào Nhà Trắng giữa hai ứng cử viên ông Donald Trump và bà Kamala Harris.', 'Tỷ phú Elon Musk phát biểu tại sự kiện ở Harrisburg, bang Pennsylvania ngày 19-10.png', 5, 0, 1409, '2024-10-29 08:19:09', '2024-10-29 08:19:09', 19),
(26, 'New Post Title', '', 'Content of the new post', '', 1, 0, 0, '2024-10-31 06:32:03', '0000-00-00 00:00:00', 1);

